import numpy as np
def isolation_tree_path_length(data: np.ndarray) -> list[int]:
    np.random.seed(0)

    def build_tree(indices, depth=0):
        if len(indices) < 3:
            return (indices, depth)
        feature = np.random.randint(data.shape[1])
        down = np.min(data[indices, feature])
        up = np.max(data[indices, feature])
        split_val = np.random.uniform(down, up)
        left_indices = [i for i in indices if data[i, feature] <= split_val]
        right_indices = [i for i in indices if data[i, feature] > split_val]
        left_tree = build_tree(left_indices, depth + 1)
        right_tree = build_tree(right_indices, depth + 1)
        return (left_tree, right_tree)

    def traverse_tree(tree, sample_idx, depth=0):
        if isinstance(tree, tuple) and len(tree) == 2:
            (leaf_indices, leaf_depth) = tree
            if sample_idx in leaf_indices:
                return leaf_depth
        else:
            (left_tree, right_tree) = tree
            feature = np.random.randint(data.shape[1])
            split_val = np.random.uniform(np.min(data[:, feature]), np.max(data[:, feature]))
            if data[sample_idx, feature] <= split_val:
                return traverse_tree(left_tree, sample_idx, depth + 1)
            else:
                return traverse_tree(right_tree, sample_idx, depth + 1)
    root = build_tree(np.arange(data.shape[0]))
    depths = [traverse_tree(root, i) for i in range(data.shape[0])]
    return depths

import numpy as np
def softplus(x, deriv=False):
    """
    Compute the SoftPlus activation function or its derivative (sigmoid) on the input.

    Parameters:
    - x: A scalar, Python list, or NumPy array of real values.
    - deriv: A Boolean flag indicating whether to compute the SoftPlus function (False)
             or its derivative (True).

    Returns:
    - A Python list of the computed values rounded to the 4th decimal place, or a single
      float if the input is a scalar.
    """
    x = np.array(x)
    if deriv:
        result = 1 / (1 + np.exp(-x))
    else:
        result = np.log1p(np.exp(-np.abs(x))) + np.maximum(x, 0)
    result_rounded = np.round(result, 4)
    if isinstance(x, (list, np.ndarray)):
        return result_rounded.tolist()
    else:
        return float(result_rounded)

