from typing import List
def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions:
        return []

    def create_tree(transactions, min_support):
        item_counts = Counter((item for transaction in transactions for item in transaction))
        frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
        sorted_items = sorted(frequent_items.items(), key=lambda x: (-x[1], x[0]))
        sorted_items = [item for (item, count) in sorted_items]
        fp_tree = FPNode(None)
        header_table = defaultdict(list)
        for transaction in transactions:
            trans = [item for item in transaction if item in frequent_items]
            trans.sort(key=lambda x: sorted_items.index(x))
            if trans:
                insert_tree(fp_tree, header_table, trans)
        return (fp_tree, header_table, sorted_items)

    def insert_tree(tree, header_table, trans):
        if not trans:
            return
        item = trans[0]
        if item not in tree.children:
            tree.children[item] = FPNode(item)
            header_table[item].append(tree.children[item])
        tree.children[item].inc()
        if len(trans) > 1:
            insert_tree(tree.children[item], header_table, trans[1:])

    def mine_tree(header_table, prefix, frequent_itemsets):
        if not header_table:
            return
        for base_pattern in header_table.keys():
            new_prefix = prefix.copy()
            new_prefix.append(base_pattern)
            frequent_itemsets.append(new_prefix)
            conditional_patterns = []
            for node in header_table[base_pattern]:
                pattern = []
                while node.parent.item is not None:
                    node = node.parent
                    pattern.append(node.item)
                if pattern:
                    pattern.reverse()
                    conditional_patterns.append((pattern, node.count))
            (conditional_tree, conditional_header) = create_conditional_tree(conditional_patterns, min_support)
            if conditional_header is not None:
                mine_tree(conditional_header, new_prefix, frequent_itemsets)

    def create_conditional_tree(patterns, min_support):
        if not patterns:
            return (None, None)
        item_counts = Counter((item for (pattern, count) in patterns for item in pattern for _ in range(count)))
        frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
        if not frequent_items:
            return (None, None)
        sorted_items = sorted(frequent_items.items(), key=lambda x: (-x[1], x[0]))
        sorted_items = [item for (item, count) in sorted_items]
        conditional_tree = FPNode(None)
        conditional_header = defaultdict(list)
        for (pattern, count) in patterns:
            if pattern:
                insert_tree(conditional_tree, conditional_header, pattern)
        return (conditional_tree, conditional_header)

    class FPNode:

        def __init__(self, item):
            self.item = item
            self.count = 0
            self.children = {}

        def inc(self):
            self.count += 1
    (fp_tree, header_table, _) = create_tree(transactions, min_support)
    frequent_itemsets = []
    mine_tree(header_table, [], frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
