import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement the k-Nearest Neighbors classifier."""
    num_test = X_test.shape[0]
    y_pred = np.zeros(num_test, dtype=y_train.dtype)

    def euclidean_distance(x1, x2):
        return np.sqrt(np.sum((x1 - x2) ** 2))

    def manhattan_distance(x1, x2):
        return np.sum(np.abs(x1 - x2))

    def cosine_distance(x1, x2):
        dot_product = np.dot(x1, x2)
        norm_x1 = np.sqrt(np.dot(x1, x1))
        norm_x2 = np.sqrt(np.dot(x2, x2))
        return 1 - dot_product / (norm_x1 * norm_x2)
    if metric == 'manhattan':
        distance_func = manhattan_distance
    elif metric == 'cosine':
        distance_func = cosine_distance
    else:
        distance_func = euclidean_distance
    for i in range(num_test):
        distances = np.array([distance_func(X_test[i], X_train[j]) for j in range(X_train.shape[0])])
        nearest_neighbors = np.argsort(distances)[:k]
        nearest_neighbors_labels = y_train[nearest_neighbors]
        (values, counts) = np.unique(nearest_neighbors_labels, return_counts=True)
        y_pred[i] = values[np.argmax(counts)]
    return y_pred

