def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)
    weights = np.ones(len(y_train)) / len(y_train)
    stumps = []
    alphas = []
    for _ in range(n_estimators):
        (stump, min_error, polarity) = find_best_stump(X_train, y_train, weights)
        epsilon = min_error + 1e-10
        alpha = 0.5 * np.log((1 - epsilon) / epsilon)
        alphas.append(alpha)
        stumps.append((stump, polarity))
        predictions = predict_stump(X_train, stump, polarity)
        weights *= np.exp(-alpha * y_train * predictions)
        weights /= np.sum(weights)
        if min_error == 0:
            break
    test_predictions = np.zeros(len(X_test))
    for (alpha, (stump, polarity)) in zip(alphas, stumps):
        test_predictions += alpha * predict_stump(X_test, stump, polarity)
    predictions = np.where(test_predictions >= 0, 1, 0).tolist()
    return predictions
def find_best_stump(X_train, y_train, weights):
    best_stump = None
    best_error = float('inf')
    best_polarity = 1
    for feature_idx in range(X_train.shape[1]):
        for threshold in np.unique(X_train[:, feature_idx]):
            for polarity in [1, -1]:
                stump_error = calculate_error(X_train, y_train, weights, feature_idx, threshold, polarity)
                if stump_error < best_error:
                    best_stump = (feature_idx, threshold)
                    best_error = stump_error
                    best_polarity = polarity
    return (best_stump, best_error, best_polarity)
def calculate_error(X_train, y_train, weights, feature_idx, threshold, polarity):
    predictions = np.where(polarity * X_train[:, feature_idx] < polarity * threshold, 1, -1)
    return np.sum(weights[y_train != predictions])
def predict_stump(X, stump, polarity):
    (feature_idx, threshold) = stump
    return np.where(polarity * X[:, feature_idx] < polarity * threshold, 1, -1)

import numpy as np
def gini_impurity(labels):
    """Calculate the Gini impurity for a set of labels."""
    (_, counts) = np.unique(labels, return_counts=True)
    probabilities = counts / len(labels)
    return 1 - np.sum(probabilities ** 2)
def best_split(X, y):
    """Find the best split for the given data according to Gini impurity."""
    (best_feature, best_threshold, best_gini_gain) = (None, None, -1)
    parent_gini = gini_impurity(y)
    for feature in range(X.shape[1]):
        sorted_indices = np.argsort(X[:, feature])
        for i in range(1, len(X)):
            if X[sorted_indices[i - 1], feature] != X[sorted_indices[i], feature]:
                threshold = (X[sorted_indices[i - 1], feature] + X[sorted_indices[i], feature]) / 2
                left_labels = y[X[:, feature] <= threshold]
                right_labels = y[X[:, feature] > threshold]
                left_gini = gini_impurity(left_labels)
                right_gini = gini_impurity(right_labels)
                gini_gain = parent_gini - len(left_labels) / len(y) * left_gini - len(right_labels) / len(y) * right_gini
                if gini_gain > best_gini_gain:
                    best_gini_gain = gini_gain
                    best_feature = feature
                    best_threshold = threshold
    return (best_feature, best_threshold, best_gini_gain > 0)
def majority_class(labels):
    """Return the majority class in the given labels."""
    (values, counts) = np.unique(labels, return_counts=True)
    index = np.argmax(counts)
    return values[index]
def build_tree(X, y, depth, max_depth):
    """Recursively build the decision tree."""
    if max_depth is not None and depth >= max_depth or len(np.unique(y)) == 1:
        return {'label': majority_class(y)}
    (feature, threshold, can_split) = best_split(X, y)
    if not can_split:
        return {'label': majority_class(y)}
    left_indices = X[:, feature] <= threshold
    right_indices = X[:, feature] > threshold
    left_tree = build_tree(X[left_indices], y[left_indices], depth + 1, max_depth)
    right_tree = build_tree(X[right_indices], y[right_indices], depth + 1, max_depth)
    return {'feature': feature, 'threshold': threshold, 'left': left_tree, 'right': right_tree}
def predict_tree(tree, X):
    """Predict the class labels for the given data using the decision tree."""
    if 'label' in tree:
        return tree['label']
    (feature, threshold) = (tree['feature'], tree['threshold'])
    if X[feature] <= threshold:
        return predict_tree(tree['left'], X)
    else:
        return predict_tree(tree['right'], X)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    tree = build_tree(X_train, y_train, 0, max_depth)
    return [predict_tree(tree, x) for x in X_test]

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate(v1, v2):
        if not (isinstance(v1, (list, tuple, np.ndarray)) and isinstance(v2, (list, tuple, np.ndarray))):
            return False
        if len(v1) != len(v2) or len(v1) == 0:
            return False
        try:
            v1 = np.array(v1, dtype=float)
            v2 = np.array(v2, dtype=float)
        except ValueError:
            return False
        return True

    def euclidean(v1, v2):
        return round(np.linalg.norm(v1 - v2), 4)

    def manhattan(v1, v2):
        return round(np.sum(np.abs(v1 - v2)), 4)

    def chebyshev(v1, v2):
        return round(np.max(np.abs(v1 - v2)), 4)

    def cosine(v1, v2):
        norm1 = np.linalg.norm(v1)
        norm2 = np.linalg.norm(v2)
        if norm1 == 0 or norm2 == 0:
            return -1
        return round(1 - np.dot(v1, v2) / (norm1 * norm2), 4)

    def d(v1, v2):
        if not validate(v1, v2):
            return -1
        if name == 'euclidean':
            return euclidean(v1, v2)
        elif name == 'manhattan':
            return manhattan(v1, v2)
        elif name == 'chebyshev':
            return chebyshev(v1, v2)
        elif name == 'cosine':
            return cosine(v1, v2)
        else:
            return -1
    return d

def calculate_score(hand: list[int]) -> int:
    """Calculate the score of a hand in Blackjack, considering the 'usable ace' rule."""
    score = sum(hand)
    aces = hand.count(1)
    while aces > 0 and score + 10 <= 21:
        score += 10
        aces -= 1
    return score
def is_natural_blackjack(hand: list[int]) -> bool:
    """Check if the hand is a natural blackjack (1 Ace and 1 card of value 10)."""
    return len(hand) == 2 and 1 in hand and (10 in hand)
def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score > 21:
        return -1
    elif dealer_score > 21:
        return 1
    elif is_natural_blackjack(player) and len(player) == 2 and (not is_natural_blackjack(dealer)):
        return 1.5
    elif player_score > dealer_score:
        return 1
    elif player_score < dealer_score:
        return -1
    else:
        return 0

import numpy as np
def sigmoid(z):
    """Compute the element-wise sigmoid (logistic) function.

    The function must work for scalar numbers, Python lists and NumPy arrays and
    must be numerically stable for very large positive or negative inputs.

    Args:
        z: A scalar (int/float) or array-like object (list or np.ndarray)
           containing numeric values.

    Returns:
        float | list: The sigmoid of the input rounded to 4 decimal places.
        If `z` is a scalar the return value is a float, otherwise it is a Python
        list with the same shape as the input.
    """
    if isinstance(z, list):
        z = np.array(z)
    elif not isinstance(z, np.ndarray):
        if z >= 0:
            return round(1 / (1 + np.exp(-z)), 4)
        else:
            return round(np.exp(z) / (1 + np.exp(z)), 4)
    result = np.where(z >= 0, 1 / (1 + np.exp(-z)), np.exp(z) / (1 + np.exp(z)))
    return round(result, 4).tolist() if isinstance(z, np.ndarray) else round(result, 4)
def round(x, n):
    if isinstance(x, np.ndarray):
        return np.around(x, decimals=n)
    else:
        return round(x, n)

