import itertools
def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    transactions = list(map(set, transactions))
    num_transactions = len(transactions)
    min_sup_count = min_sup * num_transactions
    candidates = set((item for transaction in transactions for item in transaction))
    candidates = {frozenset([item]) for item in candidates}
    frequent_itemsets = []
    while candidates:
        itemset_counts = {candidate: 0 for candidate in candidates}
        for transaction in transactions:
            for candidate in candidates:
                if candidate.issubset(transaction):
                    itemset_counts[candidate] += 1
        frequent_candidates = {candidate for (candidate, count) in itemset_counts.items() if count >= min_sup_count}
        frequent_itemsets.extend(sorted(frequent_candidates))
        candidates = generate_candidates(frequent_candidates, len(frequent_candidates[0]) + 1)
    return sorted((tuple(itemset) for itemset in frequent_itemsets))
def generate_candidates(frequent_itemsets, k):
    """Generate candidate itemsets of size k from frequent itemsets of size k-1."""
    candidates = set()
    for itemset1 in frequent_itemsets:
        for itemset2 in frequent_itemsets:
            candidate = itemset1.union(itemset2)
            if len(candidate) == k and is_valid_candidate(candidate, frequent_itemsets):
                candidates.add(candidate)
    return candidates
def is_valid_candidate(candidate, frequent_itemsets):
    """Check if candidate is valid by ensuring all its subsets of size k-1 are frequent."""
    for subset in itertools.combinations(candidate, len(candidate) - 1):
        if frozenset(subset) not in frequent_itemsets:
            return False
    return True
