import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """

    def build_tree(data, height_limit):
        if data.shape[0] <= 1 or height_limit == 0:
            return {'data': data, 'left': None, 'right': None}
        feature = np.random.randint(data.shape[1])
        (min_val, max_val) = (data[:, feature].min(), data[:, feature].max())
        if min_val == max_val:
            return {'data': data, 'left': None, 'right': None}
        threshold = np.random.uniform(min_val, max_val)
        left_idx = data[:, feature] < threshold
        right_idx = data[:, feature] >= threshold
        return {'data': data, 'left': build_tree(data[left_idx], height_limit - 1), 'right': build_tree(data[right_idx], height_limit - 1), 'feature': feature, 'threshold': threshold}

    def path_length(tree, x, height_limit):
        if tree['data'].shape[0] <= 1 or height_limit == 0:
            n = tree['data'].shape[0]
            return path_length_correction(n)
        if x[tree['feature']] < tree['threshold']:
            return 1 + path_length(tree['left'], x, height_limit - 1)
        else:
            return 1 + path_length(tree['right'], x, height_limit - 1)

    def path_length_correction(n):
        if n > 1:
            return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
        return 0
    n_samples = data.shape[0]
    height_limit = int(math.ceil(math.log2(sample_size)))
    trees = []
    for _ in range(n_trees):
        if sample_size < n_samples:
            indices = np.random.choice(n_samples, sample_size, replace=False)
            sample = data[indices]
        else:
            sample = data
        trees.append(build_tree(sample, height_limit))
    scores = np.zeros(n_samples)
    for i in range(n_samples):
        path_lengths = [path_length(tree, data[i], height_limit) for tree in trees]
        scores[i] = 2 ** (-np.mean(path_lengths) / (2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples))
    threshold = np.quantile(scores, 1 - epsilon)
    outliers = np.where(scores >= threshold)[0]
    return sorted(outliers.tolist())
