import random
def kmeans(X: np.ndarray, K: int, max_iter: int=100, random_state: int | None=None) -> list[list[float]]:
    """Perform K-Means clustering with K-Means++ initialisation."""
    if random_state is not None:
        np.random.seed(random_state)
        random.seed(random_state)
    centroids = [X[np.random.choice(X.shape[0])].tolist()]
    for _ in range(1, K):
        dists = np.min([np.sum((X - c) ** 2, axis=1) for c in centroids], axis=0)
        probs = dists / np.sum(dists)
        cumprobs = probs.cumsum()
        r = random.random()
        for (j, p) in enumerate(cumprobs):
            if r < p:
                i = j
                break
        centroids.append(X[i].tolist())
    centroids = np.array(centroids)
    for _ in range(max_iter):
        distances = np.sqrt(((X - centroids[:, np.newaxis]) ** 2).sum(axis=2))
        labels = np.argmin(distances, axis=0)
        new_centroids = centroids.copy()
        for k in range(K):
            if (labels == k).any():
                new_centroids[k] = np.mean(X[labels == k], axis=0)
        if np.allclose(centroids, new_centroids):
            break
        centroids = new_centroids
    centroids = np.round(centroids, 4)
    centroids = sorted(centroids, key=lambda c: c[0])
    return centroids.tolist()

