def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement the k-Nearest Neighbors (k-NN) classifier."""

    def euclidean_distance(x1, x2):
        return np.sqrt(np.sum((x1 - x2) ** 2))

    def manhattan_distance(x1, x2):
        return np.sum(np.abs(x1 - x2))

    def cosine_distance(x1, x2):
        return 1 - np.dot(x1, x2) / (np.sqrt(np.dot(x1, x1)) * np.sqrt(np.dot(x2, x2)))
    if metric == 'euclidean':
        dist_func = euclidean_distance
    elif metric == 'manhattan':
        dist_func = manhattan_distance
    elif metric == 'cosine':
        dist_func = cosine_distance
    else:
        dist_func = euclidean_distance
    predictions = np.zeros(X_test.shape[0])
    for (i, test_sample) in enumerate(X_test):
        distances = np.array([dist_func(test_sample, train_sample) for train_sample in X_train])
        nearest_indices = np.argsort(distances)[:k]
        nearest_labels = y_train[nearest_indices]
        (unique_labels, counts) = np.unique(nearest_labels, return_counts=True)
        max_count = np.max(counts)
        max_count_indices = np.where(counts == max_count)[0]
        if len(max_count_indices) == 1:
            predictions[i] = unique_labels[np.argmax(counts)]
        else:
            predictions[i] = min(unique_labels[max_count_indices])
    return predictions
