def find_best_split(X, y, features):
    """Find the best split feature and threshold based on the maximum Gini gain."""
    best_gini_gain = 0
    best_feature = None
    best_threshold = None
    for feature in features:
        sorted_indices = np.argsort(X[:, feature])
        sorted_X = X[sorted_indices, feature]
        sorted_y = y[sorted_indices]
        for i in range(1, len(sorted_X)):
            if sorted_X[i] != sorted_X[i - 1]:
                threshold = (sorted_X[i] + sorted_X[i - 1]) / 2
                left_y = sorted_y[:i]
                right_y = sorted_y[i:]
                gini_left = gini_impurity(left_y)
                gini_right = gini_impurity(right_y)
                gini_split = (len(left_y) * gini_left + len(right_y) * gini_right) / len(y)
                gini_gain = gini_impurity(y) - gini_split
                if gini_gain > best_gini_gain:
                    best_gini_gain = gini_gain
                    best_feature = feature
                    best_threshold = threshold
