def build_tree(X, y, depth, max_depth, features):
    """Recursively build the decision tree."""
    if np.all(y == y[0]) or (max_depth is not None and depth >= max_depth):
        return {'label': np.argmax(np.bincount(y))}
    (best_feature, best_threshold) = find_best_split(X, y, features)
    if best_feature is None:
        return {'label': np.argmax(np.bincount(y))}
    left_indices = X[:, best_feature] < best_threshold
    right_indices = X[:, best_feature] >= best_threshold
    if np.all(left_indices) or np.all(~left_indices):
        return {'label': np.argmax(np.bincount(y))}
    left_tree = build_tree(X[left_indices], y[left_indices], depth + 1, max_depth, features)
    right_tree = build_tree(X[right_indices], y[right_indices], depth + 1, max_depth, features)
    return {'feature': best_feature, 'threshold': best_threshold, 'left': left_tree, 'right': right_tree}
