def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model."""
    if N < 1:
        raise ValueError('N must be at least 1')
    corpus_tokens = [sentence.split() for sentence in corpus]
    bol_token = '<bol>'
    eol_token = '<eol>'
    if N > 1:
        for sentence in corpus_tokens:
            sentence[:0] = [bol_token] * (N - 1)
            sentence.append(eol_token)
    ngrams = defaultdict(int)
    prefix_ngrams = defaultdict(int)
    for sentence in corpus_tokens:
        for i in range(len(sentence) - N + 1):
            ngram = tuple(sentence[i:i + N])
            ngrams[ngram] += 1
        for i in range(len(sentence) - N + 2):
            prefix = tuple(sentence[i:i + N - 1])
            prefix_ngrams[prefix] += 1
    query_tokens = query.split()
    if N > 1:
        query_tokens[:0] = [bol_token] * (N - 1)
        query_tokens.append(eol_token)
    total_log_prob = 0.0
    for i in range(len(query_tokens) - N + 1):
        ngram = tuple(query_tokens[i:i + N])
        prefix = tuple(query_tokens[i:i + N - 1])
        if ngrams[ngram] == 0 or prefix_ngrams[prefix] == 0:
            return float('-inf')
        prob = ngrams[ngram] / prefix_ngrams[prefix]
        total_log_prob += math.log(prob)
    return round(total_log_prob, 4)
