def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    def mse(y):
        return np.mean((y - np.mean(y)) ** 2)

    def split_node(X, y, feature_idx, threshold):
        left_mask = X[:, feature_idx] <= threshold
        right_mask = X[:, feature_idx] > threshold
        return (left_mask, right_mask)

    def find_best_split(X, y):
        (best_feature, best_threshold, best_mse) = (None, None, float('inf'))
        for feature_idx in range(X.shape[1]):
            for threshold in np.unique(X[:, feature_idx]):
                (left_mask, right_mask) = split_node(X, y, feature_idx, threshold)
                if any((mask.sum() == 0 for mask in [left_mask, right_mask])):
                    continue
                (mse_left, mse_right) = (mse(y[left_mask]), mse(y[right_mask]))
                mse_split = (mse_left * left_mask.sum() + mse_right * right_mask.sum()) / X.shape[0]
                if mse_split < best_mse:
                    (best_mse, best_feature, best_threshold) = (mse_split, feature_idx, threshold)
        return (best_feature, best_threshold)

    def build_tree(X, y, depth=0):
        if depth >= max_depth or len(y) == 0 or mse(y) == 0:
            return np.mean(y)
        (feature_idx, threshold) = find_best_split(X, y)
        if feature_idx is None:
            return np.mean(y)
        (left_mask, right_mask) = split_node(X, y, feature_idx, threshold)
        left_tree = build_tree(X[left_mask], y[left_mask], depth + 1)
        right_tree = build_tree(X[right_mask], y[right_mask], depth + 1)
        return {'feature_idx': feature_idx, 'threshold': threshold, 'left': left_tree, 'right': right_tree}

    def predict_tree(x, tree):
        if isinstance(tree, np.ndarray):
            return tree
        if x[tree['feature_idx']] <= tree['threshold']:
            return predict_tree(x, tree['left'])
        else:
            return predict_tree(x, tree['right'])

    def predict(X, trees):
        predictions = np.zeros(X.shape[0])
        for tree in trees:
            predictions += learning_rate * np.array([predict_tree(x, tree) for x in X])
        return predictions
    F = np.mean(y_train)
