def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    if not X_train:
        return []
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)

    def sse(y):
        return np.sum((y - np.mean(y)) ** 2)

    def build_tree(X, y, depth):
        n_samples = X.size
        if depth == max_depth or n_samples < 2:
            return np.mean(y)
        best_split = None
        best_sse = float('inf')
        for i in range(1, n_samples):
            left = y[X <= X[i]]
            right = y[X > X[i]]
            current_sse = sse(left) + sse(right)
            if current_sse < best_sse:
                best_sse = current_sse
                best_split = X[i]
        if best_split is None:
            return np.mean(y)
        left_indices = X <= best_split
        right_indices = X > best_split
        left_tree = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_tree = build_tree(X[right_indices], y[right_indices], depth + 1)
        return (best_split, left_tree, right_tree)

    def predict_point(x, tree):
        if isinstance(tree, (float, int)):
            return tree
        (split, left, right) = tree
        if x <= split:
            return predict_point(x, left)
        else:
            return predict_point(x, right)

    def predict(X, tree):
        return [predict_point(x, tree) for x in X]
    tree = build_tree(X_train, y_train, 0)
    predictions = predict(X_test, tree)
    return predictions
