def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    sorted_items = sorted(frequent_items.items(), key=lambda x: (-x[1], x[0]))
    filtered_transactions = [[item for item in transaction if item in frequent_items] for transaction in transactions]
    for transaction in filtered_transactions:
        transaction.sort(key=lambda x: sorted_items.index((x, item_counts[x])))
    root = FPNode(None, None)
    for transaction in filtered_transactions:
        root.add(transaction)
    header_table = root.make_header_table()
    freq_item_sets = set()

    def mine_tree(tree, prefix):
        for (item, count) in tree.items():
            new_prefix = tuple(sorted(prefix + (item,)))
            freq_item_sets.add(new_prefix)
            cond_pattern_base = tree.get_cond_pattern_base(item)
            (cond_tree, _) = build_tree(cond_pattern_base, min_sup)
            if cond_tree:
                mine_tree(cond_tree, new_prefix)
    mine_tree(header_table, ())
    return sorted(map(tuple, freq_item_sets), key=lambda x: (len(x), x))
class FPNode:

    def __init__(self, item, count, parent=None):
        self.item = item
        self.count = count
        self.parent = parent
        self.children = {}
        self.link = None

    def add(self, transaction):
        if transaction:
            first_item = transaction[0]
            if first_item in self.children:
                self.children[first_item].count += 1
            else:
                child = FPNode(first_item, 1, self)
                self.children[first_item] = child
                if self.item is not None:
                    current = self.link
                    while current is not None:
                        current = current.link
                    current = child
            self.children[first_item].add(transaction[1:])

    def make_header_table(self):
        header_table = defaultdict(list)
        for child in self.children.values():
            header_table[child.item].append(child)
        return header_table

    def get_cond_pattern_base(self, item):
        patterns = []
        for node in self.items[item]:
            pattern = []
            current = node
            while current.parent is not None:
                current = current.parent
                if current.item is not None:
                    pattern.append(current.item)
            if pattern:
                patterns.append((tuple(pattern), node.count))
        return patterns
def build_tree(patterns, min_sup):
    root = FPNode(None, None)
    item_counts = Counter()
    for (pattern, count) in patterns:
        for item in pattern:
            item_counts[item] += count
        root.add(pattern)
    return (root, item_counts)

