def contrastive_divergence(X: np.ndarray, W: np.ndarray, hbias: np.ndarray, vbias: np.ndarray, learning_rate: float, k: int) -> list[list[float]]:
    """Perform one CD-k weight update for a binary RBM."""
    if X.shape[0] == 0:
        return []
    (m, n_v) = X.shape
    n_h = W.shape[1]
    h0_prob = sigmoid(np.dot(X, W) + hbias)
    v_prob = X
    h_prob = h0_prob
    for _ in range(k):
        h_prob = sigmoid(np.dot(v_prob, W) + hbias)
        v_prob = sigmoid(np.dot(h_prob, W.T) + vbias)
    h_k_prob = h_prob
    pos_grad = np.dot(X.T, h0_prob)
    neg_grad = np.dot(v_prob.T, h_k_prob)
    W_new = W + learning_rate * (pos_grad - neg_grad) / m
    return np.round(W_new, 4).tolist()

import numpy as np
def sigmoid(z):
    """Compute the numerically stable, element-wise sigmoid activation.

    The function must work for a scalar, list/tuple, or NumPy ndarray input.

    Args:
        z: int, float, list, tuple, or numpy.ndarray – input values.

    Returns:
        float if *z* is a scalar, otherwise a Python list obtained with
        ``numpy.ndarray.tolist()``, where every element is the sigmoid of the
        corresponding input value and is rounded to four decimal places.
    """

    def stable_sigmoid(x):
        if x >= 0:
            return 1 / (1 + np.exp(-x))
        else:
            return np.exp(x) / (1 + np.exp(x))
    if isinstance(z, (list, tuple)):
        z = np.array(z)
    elif not isinstance(z, (int, float, np.ndarray)):
        raise ValueError('Input must be an int, float, list, tuple, or numpy.ndarray.')
    if isinstance(z, np.ndarray):
        result = np.vectorize(stable_sigmoid)(z)
        if isinstance(z, (list, tuple)):
            result = result.tolist()
    else:
        result = stable_sigmoid(z)
    if isinstance(result, (float, np.float64)):
        return round(result, 4)
    else:
        return [round(x, 4) for x in result]

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def compute_grad(element):
        return np.round(element * (1 - element), 4)

    def apply_grad_recursive(input_data):
        if isinstance(input_data, (float, int)):
            return compute_grad(input_data)
        elif isinstance(input_data, (list, np.ndarray)):
            return [apply_grad_recursive(element) for element in input_data]
        else:
            raise ValueError('Unsupported data type. Only scalars, lists, and NumPy arrays are allowed.')
    return apply_grad_recursive(z)

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    if task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

