def split_node(data):
    """Randomly choose a feature and a threshold to split the node."""
    feature_idx = np.random.randint(data.shape[1])
    min_val = data[:, feature_idx].min()
    max_val = data[:, feature_idx].max()
    if min_val == max_val:
        threshold = min_val
    else:
        threshold = np.random.uniform(min_val, max_val)
    return (feature_idx, threshold)
def build_tree(data, height_limit, current_depth):
    """Recursively build a binary tree."""
    if current_depth >= height_limit or len(data) <= 1:
        return data
    (feature_idx, threshold) = split_node(data)
    left_data = data[data[:, feature_idx] < threshold]
    right_data = data[data[:, feature_idx] >= threshold]
    left_child = build_tree(left_data, height_limit, current_depth + 1)
    right_child = build_tree(right_data, height_limit, current_depth + 1)
    return (feature_idx, threshold, left_child, right_child)
def path_length(node, x, current_depth):
    """Calculate the path length for a given point."""
    if len(node) == 1:
        return current_depth + c(len(node))
    (feature_idx, threshold, left_child, right_child) = node
    if x[feature_idx] < threshold:
        return path_length(left_child, x, current_depth + 1)
    else:
        return path_length(right_child, x, current_depth + 1)
def c(n):
    """Compute the expected path length of unsuccessful searches in a BST."""
    if n <= 1:
        return 0
    return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
def anomaly_score(average_path_length, n_samples):
    """Compute the anomaly score for a given average path length."""
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    return 2 ** (-average_path_length / phi)
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    (n_samples, n_features) = data.shape
    if epsilon == 0:
        return []
    tree_height_limit = math.ceil(math.log2(sample_size))
    path_lengths = np.zeros(n_samples)
    for _ in range(n_trees):
        if sample_size >= n_samples:
            sample = data
        else:
            sample_indices = np.random.choice(n_samples, sample_size, replace=False)
            sample = data[sample_indices]
        tree = build_tree(sample, tree_height_limit, 0)
        for i in range(n_samples):
            path_lengths[i] += path_length(tree, data[i], 0)
    path_lengths /= n_trees
    scores = [anomaly_score(pl, n_samples) for pl in path_lengths]
    threshold = sorted(scores)[int(n_samples * (1 - epsilon))]
    return sorted([i for (i, score) in enumerate(scores) if score > threshold])
