def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:

    def empirical_expectation(X, y):
        """Compute the empirical expectation of each feature given the label."""
        feature_expectation = defaultdict(float)
        for (x, label) in zip(X, y):
            for (feature_index, feature_value) in enumerate(x):
                feature_expectation[feature_index, feature_value, label] += 1
        return feature_expectation

    def model_expectation(X, y, weights):
        """Compute the model expectation of each feature given the label."""
        feature_expectation = defaultdict(float)
        for (x, label) in zip(X, y):
            partition_function = sum((math.exp(sum((weights[fv] for fv in get_features(x, y_)))) for y_ in np.unique(y_train)))
            for label_ in np.unique(y_train):
                prob = math.exp(sum((weights[fv] for fv in get_features(x, label_)))) / partition_function
                for (feature_index, feature_value) in enumerate(x):
                    feature_expectation[feature_index, feature_value, label_] += prob
        return feature_expectation

    def get_features(x, label):
        """Get the features (feature_index, feature_value) for a given sample and label."""
        return [(feature_index, feature_value, label) for (feature_index, feature_value) in enumerate(x)]

    def update_weights(weight_dict, empirical, model, labels):
        """Update the weights using Generalized Iterative Scaling."""
        for feature in empirical:
            weight_dict[feature] += math.log((empirical[feature] + 1e-10) / (model[feature] + 1e-10))
        return weight_dict

    def predict(x, weights):
        """Predict the label for a given sample."""
        scores = []
        for label in np.unique(y_train):
            score = sum((weights[fv] for fv in get_features(x, label)))
            scores.append((label, score))
        return max(scores, key=lambda x: x[1])[0]
    weights = defaultdict(float)
    empirical_expectation_dict = empirical_expectation(X_train, y_train)
    for _ in range(n_iter):
        model_expectation_dict = model_expectation(X_train, y_train, weights)
        prev_weights = weights.copy()
        weights = update_weights(weights, empirical_expectation_dict, model_expectation_dict, np.unique(y_train))
        if all((abs(weights[fv] - prev_weights[fv]) < epsilon for fv in weights)):
