def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    N = len(transactions)
    min_support_count = math.ceil(minsup * N)
    items = set((item for transaction in transactions for item in transaction))
    itemsets = {item: 0 for item in items}
    for transaction in transactions:
        for item in transaction:
            itemsets[item] += 1
    frequent_itemsets = {}
    for (item, count) in itemsets.items():
        if count >= min_support_count:
            frequent_itemsets[tuple([item])] = count
    k = 2
    while True:
        candidate_itemsets = generate_candidates(list(frequent_itemsets.keys()), k)
        if not candidate_itemsets:
            break
        candidate_counts = Counter()
        for transaction in transactions:
            transaction_set = set(transaction)
            for candidate in candidate_itemsets:
                if set(candidate).issubset(transaction_set):
                    candidate_counts[candidate] += 1
        for (candidate, count) in candidate_counts.items():
            if count >= min_support_count:
                frequent_itemsets[candidate] = count
        if not frequent_itemsets:
            break
        k += 1
    return frequent_itemsets
def generate_candidates(current_itemsets: List[Tuple[Hashable]], k: int) -> List[Tuple[Hashable]]:
    """Generate candidate itemsets of size k from the given frequent itemsets."""
    candidates = set()
    for i in range(len(current_itemsets)):
        for j in range(i + 1, len(current_itemsets)):
            candidate = tuple(sorted(set(current_itemsets[i]) | set(current_itemsets[j])))
            if len(candidate) == k:
                candidates.add(candidate)
    return list(candidates)
