def euclidean_distance(point1: np.ndarray, point2: np.ndarray) -> float:
    """Calculate the Euclidean distance between two points."""
    return np.linalg.norm(point1 - point2)
def initialize_centroids_kmeans_plus_plus(X: np.ndarray, K: int, random_state: int | None) -> np.ndarray:
    """Initialize centroids for K-Means++."""
    np.random.seed(random_state)
    centroids = [X[np.random.choice(X.shape[0]), :]]
    for _ in range(1, K):
        dist_sq = np.array([min([euclidean_distance(x, c) ** 2 for c in centroids]) for x in X])
        probs = dist_sq / dist_sq.sum()
        cumprobs = probs.cumsum()
        r = np.random.rand()
        for (j, p) in enumerate(cumprobs):
            if r < p:
                i = j
                break
        centroids.append(X[i, :])
    return np.array(centroids)
def assign_points_to_centroids(X: np.ndarray, centroids: np.ndarray) -> np.ndarray:
    """Assign each data point to the nearest centroid."""
    distances = np.array([[euclidean_distance(x, c) for c in centroids] for x in X])
    return np.argmin(distances, axis=1)
def update_centroids(X: np.ndarray, labels: np.ndarray, centroids: np.ndarray) -> np.ndarray:
    """Update the centroids based on the mean of the points assigned to them."""
    new_centroids = centroids.copy()
    for i in range(centroids.shape[0]):
        if np.any(labels == i):
            new_centroids[i] = np.mean(X[labels == i], axis=0)
    return new_centroids
def kmeans(X: np.ndarray, K: int, max_iter: int=100, random_state: int | None=None) -> List[List[float]]:
    """Perform K-Means clustering with K-Means++ initialisation."""
    random.seed(random_state)
    np.random.seed(random_state)
    centroids = initialize_centroids_kmeans_plus_plus(X, K, random_state)
    for _ in range(max_iter):
        old_labels = assign_points_to_centroids(X, centroids)
        centroids = update_centroids(X, old_labels, centroids)
        new_labels = assign_points_to_centroids(X, centroids)
        if np.array_equal(old_labels, new_labels):
            break
    centroids = np.round(centroids, decimals=4)
    sorted_centroids = sorted(centroids.tolist())
    return sorted_centroids
