def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:

    def euclidean_distance(x1, x2):
        return np.sqrt(np.sum((x1 - x2) ** 2, axis=1))

    def manhattan_distance(x1, x2):
        return np.sum(np.abs(x1 - x2), axis=1)

    def cosine_distance(x1, x2):
        dot_products = np.sum(x1 * x2, axis=1)
        norms_x1 = np.linalg.norm(x1, axis=1)
        norms_x2 = np.linalg.norm(x2, axis=1)
        return 1 - dot_products / (norms_x1 * norms_x2)
    if metric not in ['euclidean', 'manhattan', 'cosine']:
        metric = 'euclidean'
    predictions = []
    for test_sample in X_test:
        distances = None
        if metric == 'euclidean':
            distances = euclidean_distance(X_train, test_sample)
        elif metric == 'manhattan':
            distances = manhattan_distance(X_train, test_sample)
        elif metric == 'cosine':
            distances = cosine_distance(X_train, test_sample)
        nearest_indices = np.argsort(distances)[:k]
        k_nearest_labels = y_train[nearest_indices]
        (unique_labels, counts) = np.unique(k_nearest_labels, return_counts=True)
        majority_vote = unique_labels[np.argmax(counts)]
        predictions.append(majority_vote)
    return np.array(predictions)
