def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """
    Implements the AdaBoost algorithm from scratch using decision stumps as weak learners.
    """
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)
    (n_samples, n_features) = X_train.shape
    sample_weights = np.full(n_samples, 1 / n_samples)
    weak_learners = []
    learner_weights = []
    for _ in range(n_estimators):
        min_error = float('inf')
        best_stump = None
        for feature_index in range(n_features):
            thresholds = np.unique(X_train[:, feature_index])
            for threshold in thresholds:
                for polarity in [1, -1]:
                    predictions = np.where(X_train[:, feature_index] < threshold if polarity == 1 else X_train[:, feature_index] >= threshold, 1, -1)
                    error = np.sum(sample_weights[y_train != predictions])
                    if error < min_error:
                        min_error = error
                        best_stump = {'index': feature_index, 'threshold': threshold, 'polarity': polarity}
                    elif error == min_error:
                        if best_stump['index'] > feature_index:
                            best_stump = {'index': feature_index, 'threshold': threshold, 'polarity': polarity}
                        elif best_stump['index'] == feature_index:
                            if best_stump['threshold'] > threshold:
                                best_stump = {'index': feature_index, 'threshold': threshold, 'polarity': polarity}
                            elif best_stump['threshold'] == threshold:
                                if best_stump['polarity'] == -1 and polarity == 1:
                                    best_stump = {'index': feature_index, 'threshold': threshold, 'polarity': polarity}
        if min_error == 0:
            break
        else:
            predictions = np.where(X_train[:, best_stump['index']] < best_stump['threshold'] if best_stump['polarity'] == 1 else X_train[:, best_stump['index']] >= best_stump['threshold'], 1, -1)
            alpha = 0.5 * np.log((1 - min_error) / (min_error + 1e-10))
            learner_weights.append(alpha)
            sample_weights *= np.exp(-alpha * y_train * predictions)
            sample_weights /= np.sum(sample_weights)
            weak_learners.append(best_stump)
    final_predictions = np.zeros(len(X_test))
    for (learner, weight) in zip(weak_learners, learner_weights):
        predictions = np.where(X_test[:, learner['index']] < learner['threshold'] if learner['polarity'] == 1 else X_test[:, learner['index']] >= learner['threshold'], 1, -1)
        final_predictions += weight * predictions
    final_predictions = np.where(final_predictions >= 0, 1, 0)
    return final_predictions.tolist()
