import numpy as np
def gini_impurity(groups, classes):
    """Calculate the Gini impurity for a split."""
    n_instances = float(sum([len(group) for group in groups]))
    gini = 0.0
    for group in groups:
        size = float(len(group))
        if size == 0:
            continue
        score = 0.0
        for class_val in classes:
            p = [row[-1] for row in group].count(class_val) / size
            score += p * p
        gini += (1.0 - score) * (size / n_instances)
    return gini
def test_split(index, value, dataset):
    """Split a dataset based on an attribute and an attribute value."""
    (left, right) = (list(), list())
    for row in dataset:
        if row[index] < value:
            left.append(row)
        else:
            right.append(row)
    return (left, right)
def get_split(dataset, n_features):
    """Select the best split point for a dataset."""
    class_values = list(set((row[-1] for row in dataset)))
    (b_index, b_value, b_score, b_groups) = (999, 999, 999, None)
    features = list()
    while len(features) < n_features:
        f = np.random.randint(len(dataset[0]) - 1)
        if f not in features:
            features.append(f)
    for index in features:
        for row in dataset:
            groups = test_split(index, row[index], dataset)
            gini = gini_impurity(groups, class_values)
            if gini < b_score:
                (b_index, b_value, b_score, b_groups) = (index, row[index], gini, groups)
    return {'index': b_index, 'value': b_value, 'groups': b_groups}
def to_terminal(group):
    """Create a terminal node value."""
    outcomes = [row[-1] for row in group]
    return max(set(outcomes), key=outcomes.count)
def split(node, max_depth, min_size, depth, n_features):
    """Create child splits for a node or make terminal."""
    (left, right) = node['groups']
    del node['groups']
    if not left or not right:
        node['left'] = node['right'] = to_terminal(left + right)
        return
    if depth >= max_depth:
        (node['left'], node['right']) = (to_terminal(left), to_terminal(right))
        return
    if len(left) <= min_size:
        node['left'] = to_terminal(left)
    else:
        node['left'] = get_split(left, n_features)
        split(node['left'], max_depth, min_size, depth + 1, n_features)
    if len(right) <= min_size:
        node['right'] = to_terminal(right)
    else:
        node['right'] = get_split(right, n_features)
        split(node['right'], max_depth, min_size, depth + 1, n_features)
def build_tree(train, max_depth, min_size, n_features):
    """Build a decision tree."""
    root = get_split(train, n_features)
    split(root, max_depth, min_size, 1, n_features)
    return root
def predict(node, row):
    """Make a prediction with a decision tree."""
    if row[node['index']] < node['value']:
        if isinstance(node['left'], dict):
            return predict(node['left'], row)
        else:
            return node['left']
    elif isinstance(node['right'], dict):
        return predict(node['right'], row)
    else:
        return node['right']
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    train = np.hstack((X_train, y_train.reshape(-1, 1)))
    tree = build_tree(train, max_depth, 1, X_train.shape[1])
    predictions = [predict(tree, test) for test in X_test]
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function."""

    def validate(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except ValueError:
            return False
        if x.ndim != 1 or y.ndim != 1 or len(x) == 0 or (len(y) == 0) or (len(x) != len(y)):
            return False
        return (x, y)

    def euclidean(x, y):
        return round(np.linalg.norm(x - y), 4)

    def manhattan(x, y):
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        cos_sim = np.dot(x, y) / (norm_x * norm_y)
        return round(1 - cos_sim, 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}

    def d(x, y):
        validated = validate(x, y)
        if not validated:
            return -1
        (x, y) = validated
        if name in metrics:
            return metrics[name](x, y)
        else:
            return -1
    return d

def calculate_hand_value(hand: list[int]) -> int:
    """Calculate the value of a hand in Blackjack considering the rule for Aces."""
    total = sum(hand)
    aces = hand.count(1)
    while aces > 0 and total + 10 <= 21:
        total += 10
        aces -= 1
    return total
def is_natural_blackjack(hand: list[int]) -> bool:
    """Check if the hand is a natural blackjack (10-value card and an Ace)."""
    return len(hand) == 2 and 1 in hand and (10 in hand)
def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """
    player_value = calculate_hand_value(player)
    dealer_value = calculate_hand_value(dealer)
    if player_value > 21:
        return -1
    elif dealer_value > 21:
        return 1
    elif is_natural_blackjack(player):
        if dealer_value < 21:
            return 1.5
        else:
            return 0.5
    if player_value > dealer_value:
        return 1
    elif player_value < dealer_value:
        return -1
    else:
        return 0

