def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer.

    The function must return a tuple (pooled_output, grad_input), both rounded
    to the nearest 4-th decimal and converted to regular Python lists using
    `tolist()`.
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    H_out = (H - p_h) // s_h + 1
    W_out = (W - p_w) // s_w + 1
    pooled_output = np.zeros((N, C, H_out, W_out))
    for n in range(N):
        for c in range(C):
            for i in range(H_out):
                for j in range(W_out):
                    (h_start, w_start) = (i * s_h, j * s_w)
                    pooled_output[n, c, i, j] = np.mean(X[n, c, h_start:h_start + p_h, w_start:w_start + p_w])
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for i in range(H_out):
                for j in range(W_out):
                    (h_start, w_start) = (i * s_h, j * s_w)
                    grad_input[n, c, h_start:h_start + p_h, w_start:w_start + p_w] += accum_grad[n, c, i, j] / (p_h * p_w)
    pooled_output = np.round(pooled_output, decimals=4).tolist()
    grad_input = np.round(grad_input, decimals=4).tolist()
    return (pooled_output, grad_input)
