import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def compute_grad(z):
        return np.round(z * (1 - z), 4)
    if isinstance(z, (float, int)):
        return float(compute_grad(z))
    if isinstance(z, np.ndarray):
        return compute_grad(z)
    if isinstance(z, list):
        if not z:
            return []
        if isinstance(z[0], (int, float)):
            return [compute_grad(item) for item in z]
        else:
            return [sigmoid_grad(item) for item in z]

import numpy as np
def gaussian_kernel(X, gamma=2.0):
    N = X.shape[0]
    XX = np.sum(X * X, axis=1).reshape(N, 1)
    r2 = XX - 2 * X @ X.T + XX.T
    return np.exp(-gamma * r2)
def kmeans(X, k, initial_centroids=None):
    if initial_centroids is None:
        initial_centroids = X[:k]
    centroids = initial_centroids
    prev_centroids = np.zeros_like(centroids)
    labels = np.zeros(X.shape[0], dtype=int)
    while np.linalg.norm(centroids - prev_centroids) > 1e-06:
        distances = np.sum((X[:, None] - centroids) ** 2, axis=2)
        labels = np.argmin(distances, axis=1)
        prev_centroids = centroids.copy()
        for i in range(k):
            if np.sum(labels == i) > 0:
                centroids[i] = np.mean(X[labels == i], axis=0)
    sorted_indices = np.argsort(np.argmin(np.abs(X - centroids[labels][:, None]), axis=0))
    label_map = {old_label: i for (i, old_label) in enumerate(sorted_indices)}
    return [label_map[label] for label in labels]
def spectral_clustering(data: np.ndarray, n_cluster: int, gamma: float=2.0, method: str='unnormalized') -> list[int]:
    if n_cluster == 1:
        return [0] * data.shape[0]
    W = gaussian_kernel(data, gamma)
    d = np.sum(W, axis=1)
    D = np.diag(d)
    if method == 'unnormalized':
        L = D - W
    else:
        D_inv_sqrt = np.diag(1.0 / np.sqrt(d))
        L = D_inv_sqrt @ (D - W) @ D_inv_sqrt
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    eigenvectors = eigenvectors[:, :n_cluster]
    if method == 'normalized':
        norm = np.sqrt(np.sum(eigenvectors ** 2, axis=1))
        eigenvectors = (eigenvectors.T / norm).T
    return kmeans(eigenvectors, n_cluster, initial_centroids=eigenvectors[:n_cluster])
