def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    num_features = X_train.shape[1]
    labels = np.unique(y_train)
    num_labels = len(labels)
    label_indices = {label: idx for (idx, label) in enumerate(labels)}
    weights = defaultdict(float)
    Q = np.zeros((num_features, num_labels))
    for (x, y) in zip(X_train, y_train):
        label_idx = label_indices[y]
        for (feature_idx, feature_val) in enumerate(x):
            Q[feature_idx, label_idx] += 1
    Q /= len(X_train)
    empirical_counts = defaultdict(int)
    for (x, y) in zip(X_train, y_train):
        label_idx = label_indices[y]
        for (feature_idx, feature_val) in enumerate(x):
            key = (feature_idx, feature_val, label_idx)
            empirical_counts[key] += 1
    for iteration in range(n_iter):
        max_update = 0
        for (feature_idx, feature_val, label_idx) in empirical_counts.keys():
            E = 0
            for x in X_train:
                feature_val_match = x[feature_idx] == feature_val
                if not feature_val_match:
                    continue
                score = 0
                for (feat_idx, _, label_id) in empirical_counts.keys():
                    if x[feat_idx] == feature_val and label_id == label_idx:
                        score += weights[feat_idx, feature_val, label_id]
                E += math.exp(score) / math.exp(compute_normalization(x, weights, empirical_counts, labels))
            delta = math.log(Q[feature_idx, label_idx] / E)
            weights[feature_idx, feature_val, label_idx] += delta
            max_update = max(max_update, abs(delta))
        if max_update < epsilon:
            break

    def predict(x):
        best_score = -np.inf
        best_label = None
        for label in labels:
            score = 0
            label_idx = label_indices[label]
            for (feature_idx, feature_val) in enumerate(x):
                key = (feature_idx, feature_val, label_idx)
                if key in weights:
                    score += weights[key]
            if score > best_score:
                best_score = score
                best_label = label
        return best_label
    return [predict(x) for x in X_test]
def compute_normalization(x, weights, empirical_counts, labels):
    log_sum = -np.inf
    for label in labels:
        score = 0
        label_idx = label_indices[label]
        for (feature_idx, feature_val) in enumerate(x):
            key = (feature_idx, feature_val, label_idx)
            if key in weights:
                score += weights[key]
        log_sum = math.logaddexp(log_sum, score)
    return log_sum

import numpy as np
def euclidean_distance(point1, point2):
    """Calculate the Euclidean distance between two points."""
    return np.sqrt(np.sum((point1 - point2) ** 2))
def pairwise_distances(data):
    """Compute the full pairwise Euclidean distance matrix for the data."""
    n = len(data)
    dist_matrix = np.zeros((n, n))
    for i in range(n):
        for j in range(i + 1, n):
            dist_matrix[i, j] = euclidean_distance(data[i], data[j])
            dist_matrix[j, i] = dist_matrix[i, j]
    return dist_matrix
def k_distance(dist_matrix, k):
    """For each sample, find its k-distance and the indices of its k nearest neighbors."""
    n = dist_matrix.shape[0]
    k_distances = np.zeros(n)
    neighbors = [None] * n
    for i in range(n):
        sorted_indices = np.argsort(dist_matrix[i])
        k_distances[i] = dist_matrix[i, sorted_indices[k]]
        neighbors[i] = sorted_indices[:k]
    return (k_distances, neighbors)
def reachability_distance(dist_matrix, k_distances, p, q):
    """Calculate the reachability distance between points p and q."""
    return max(k_distances[q], dist_matrix[p, q])
def local_reachability_density(dist_matrix, k_distances, neighbors, p):
    """Calculate the Local Reachability Density (LRD) for point p."""
    n_neighbors = len(neighbors)
    reach_sum = sum((reachability_distance(dist_matrix, k_distances, p, q) for q in neighbors))
    return n_neighbors / reach_sum
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factors for each point and return scores and outlier indices."""
    dist_matrix = pairwise_distances(data)
    (k_distances, neighbors) = k_distance(dist_matrix, k)
    n = len(data)
    lrd_values = np.array([local_reachability_density(dist_matrix, k_distances, neighbors[i], i) for i in range(n)])
    lof_scores = np.zeros(n)
    for i in range(n):
        lof_scores[i] = sum((lrd_values[q] for q in neighbors[i])) / k / lrd_values[i]
    lof_scores_rounded = np.round(lof_scores, decimals=4)
    outlier_indices = [i for (i, score) in enumerate(lof_scores_rounded) if score > epsilon]
    return (lof_scores_rounded.tolist(), sorted(outlier_indices))
