import numpy as np
def softplus(x, deriv: bool=False):

    def safe_log1p(x):
        return np.log1p(np.exp(x))

    def safe_logistic(x):
        return 1 / (1 + np.exp(-x))
    x = np.array(x if isinstance(x, (list, np.ndarray)) else [x])
    if deriv:
        result = safe_logistic(x)
    else:
        result = np.where(x > 0, x + np.log1p(np.exp(-x)), safe_log1p(x))
    result = np.round(result, 4)
    if isinstance(x, np.ndarray) and x.size == 1:
        return float(result)
    else:
        return result.tolist()
