def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire."""
    n = len(grid)
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    if n == 1 and grid[0] == '.':
        return 0
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_queue = deque()
    agent_queue = deque([(0, 0, 0)])
    visited = set([(0, 0)])
    for r in range(n):
        for c in range(n):
            if grid[r][c] == 'F':
                fire_queue.append((r, c, 0))
    while fire_queue and agent_queue:
        spread_fire(fire_queue, grid, visited)
        if not move_agent(agent_queue, grid, visited, directions):
            break
    return grid[n - 1][n - 1] if grid[n - 1][n - 1] else -1
def move_agent(agent_queue, grid, visited, directions):
    """Helper function to move the agent safely in the grid."""
    moves_to_add = []
    while agent_queue:
        (r, c, t) = agent_queue.popleft()
        for (dr, dc) in directions:
            (nr, nc) = (r + dr, c + dc)
            if 0 <= nr < len(grid) and 0 <= nc < len(grid) and (grid[nr][nc] not in visited) and ((nr, nc) not in visited):
                if grid[nr][nc] != 'F':
                    grid[nr] = grid[nr][:nc] + str(t + 1) + grid[nr][nc + 1:]
                    moves_to_add.append((nr, nc, t + 1))
                    visited.add((nr, nc))
                    if nr == len(grid) - 1 and nc == len(grid) - 1:
                        return False
    for move in moves_to_add:
        agent_queue.append(move)
    return True
