import numpy as np
import numpy as np
def gini_impurity(y):
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def split_features(X, y, feature_index, threshold):
    left_indices = X[:, feature_index] <= threshold
    (X_left, X_right) = (X[left_indices], X[~left_indices])
    (y_left, y_right) = (y[left_indices], y[~left_indices])
    return (X_left, X_right, y_left, y_right)
def best_split_gini(X, y):
    best_gini_gain = -1
    best_threshold = None
    best_feature_index = None
    parent_gini = gini_impurity(y)
    for feature_index in range(X.shape[1]):
        sorted_indices = np.argsort(X[:, feature_index])
        unique_values = np.unique(X[:, feature_index])
        for i in range(1, len(unique_values)):
            if unique_values[i] == unique_values[i - 1]:
                continue
            threshold = (unique_values[i] + unique_values[i - 1]) / 2
            (X_left, X_right, y_left, y_right) = split_features(X, y, feature_index, threshold)
            if len(y_left) == 0 or len(y_right) == 0:
                continue
            gini_left = gini_impurity(y_left)
            gini_right = gini_impurity(y_right)
            gini_gain = parent_gini - len(y_left) / len(y) * gini_left - len(y_right) / len(y) * gini_right
            if gini_gain > best_gini_gain:
                best_gini_gain = gini_gain
                best_threshold = threshold
                best_feature_index = feature_index
    return (best_feature_index, best_threshold, best_gini_gain)
def build_tree(X, y, max_depth=None, depth=0):
    (unique_labels, counts) = np.unique(y, return_counts=True)
    majority_label = unique_labels[np.argmax(counts)]
    if np.all(y == y[0]) or (max_depth is not None and depth >= max_depth):
        return {'label': majority_label}
    (feature_index, threshold, gini_gain) = best_split_gini(X, y)
    if gini_gain == 0:
        return {'label': majority_label}
    left_indices = X[:, feature_index] <= threshold
    (X_left, X_right) = (X[left_indices], X[~left_indices])
    (y_left, y_right) = (y[left_indices], y[~left_indices])
    if len(X_left) == 0 or len(X_right) == 0:
        return {'label': majority_label}
    left_subtree = build_tree(X_left, y_left, max_depth=max_depth, depth=depth + 1)
    right_subtree = build_tree(X_right, y_right, max_depth=max_depth, depth=depth + 1)
    return {'feature_index': feature_index, 'threshold': threshold, 'left': left_subtree, 'right': right_subtree}
def predict_single(sample, tree):
    while 'label' not in tree:
        feature_index = tree['feature_index']
        threshold = tree['threshold']
        if sample[feature_index] <= threshold:
            tree = tree['left']
        else:
            tree = tree['right']
    return tree['label']
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    tree = build_tree(X_train, y_train, max_depth=max_depth)
    predictions = [predict_single(x, tree) for x in X_test]
    return predictions
