def average_path_length(n: int) -> float:
    """Calculate the average path length for a binary tree."""
    if n <= 1:
        return 0.0
    return 2.0 * math.log(n - 1) + 0.5772156649015329 - 2.0 * (n - 1) / n
def build_tree(data: np.ndarray, height_limit: int, depth: int=0) -> dict:
    """Build a randomly split isolation tree."""
    if data.shape[0] <= 1 or depth >= height_limit:
        return {'c': data.shape[0]}
    feature = np.random.randint(data.shape[1])
    (min_val, max_val) = (data[:, feature].min(), data[:, feature].max())
    if min_val == max_val:
        threshold = min_val
    else:
        threshold = np.random.uniform(min_val, max_val)
    left = data[data[:, feature] <= threshold]
    right = data[data[:, feature] > threshold]
    return {'feature': feature, 'threshold': threshold, 'left': build_tree(left, height_limit, depth + 1), 'right': build_tree(right, height_limit, depth + 1)}
def path_length(tree: dict, x: np.ndarray) -> float:
    """Calculate path length of x in the isolation tree."""
    while True:
        if 'c' in tree:
            return tree['c']
        if x[tree['feature']] <= tree['threshold']:
            tree = tree['left']
        else:
            tree = tree['right']
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> List[int]:
    """Tiny Isolation Forest implementation."""
    n_samples = data.shape[0]
    if sample_size >= n_samples:
        sample_indices = np.arange(n_samples)
    else:
        sample_indices = np.random.choice(n_samples, sample_size, replace=False)
    trees = []
    height_limit = math.ceil(math.log2(sample_size))
    for _ in range(n_trees):
        sample_data = data[sample_indices]
        trees.append(build_tree(sample_data, height_limit))
    path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        for tree in trees:
            path_lengths[i] += path_length(tree, data[i])
    path_lengths /= n_trees
    c_n = average_path_length(sample_size)
    anomaly_scores = 2.0 ** (-path_lengths / c_n)
    threshold = np.sort(anomaly_scores)[int((1 - epsilon) * n_samples)]
    outlier_indices = np.where(anomaly_scores >= threshold)[0].tolist()
    return sorted(outlier_indices)
