def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""

    def generate_ngrams(tokens, n):
        return [' '.join(tokens[i:i + n]) for i in range(len(tokens) - n + 1)]
    ngrams = generate_ngrams(corpus, N)
    ngram_counts = Counter(ngrams)
    if N > 1:
        prefix_ngrams = generate_ngrams(corpus, N - 1)
        prefix_counts = Counter(prefix_ngrams)
    else:
        prefix_counts = Counter(corpus)
    log_prob_sum = 0.0
    sequence_ngrams = generate_ngrams(sequence, N)
    for ngram in sequence_ngrams:
        if N == 1:
            prefix_count = prefix_counts[sequence[0]]
            ngram_count = ngram_counts[ngram] if ngram in ngram_counts else 0
        else:
            parts = ngram.split(' ')
            prefix_count = prefix_counts[' '.join(parts[:-1])]
            ngram_count = ngram_counts[ngram] if ngram in ngram_counts else 0
        if prefix_count == 0 or ngram_count == 0:
            return float('-inf')
        probability = ngram_count / prefix_count
        log_prob_sum += np.log(probability)
    return round(log_prob_sum, 4)
