def kmeans(X: np.ndarray, K: int, max_iter: int=100, random_state: int | None=None) -> list[list[float]]:
    """Perform K-Means clustering with K-Means++ initialisation."""
    if random_state is not None:
        np.random.seed(random_state)
        random.seed(random_state)

    def euclidean_distance(a, b):
        return np.linalg.norm(a - b)

    def initialize_centroids(X, K):
        centroids = [X[random.randint(0, len(X) - 1)]]
        for _ in range(1, K):
            distances = np.array([min([euclidean_distance(x, c) for c in centroids]) for x in X])
            probabilities = distances / distances.sum()
            cumulative_probabilities = probabilities.cumsum()
            r = random.random()
            for i in range(len(cumulative_probabilities)):
                if r < cumulative_probabilities[i]:
                    centroids.append(X[i])
                    break
        return np.array(centroids)
    centroids = initialize_centroids(X, K)
    old_centroids = np.zeros(centroids.shape)
    clusters = np.zeros(len(X))

    def assign_clusters(X, centroids):
        for i in range(len(X)):
            distances = euclidean_distance(X[i], centroids)
            cluster = np.argmin(distances)
            clusters[i] = cluster
        return clusters

    def update_centroids(X, clusters, K):
        for i in range(K):
            points = [X[j] for j in range(len(X)) if clusters[j] == i]
            if points:
                centroids[i] = np.mean(points, axis=0)
        return centroids
    for _ in range(max_iter):
        old_clusters = clusters.copy()
        clusters = assign_clusters(X, centroids)
        if np.array_equal(clusters, old_clusters):
            break
        centroids = update_centroids(X, clusters, K)
    centroids = np.round(centroids, decimals=4)
    centroids = sorted(centroids, key=lambda x: (x[0], tuple(x)))
    return centroids.tolist()

