def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:

    def compute_distances(X_train, X_test_sample, metric):
        if metric == 'euclidean':
            return np.sqrt(((X_train - X_test_sample) ** 2).sum(axis=1))
        elif metric == 'manhattan':
            return np.abs(X_train - X_test_sample).sum(axis=1)
        elif metric == 'cosine':
            norms = np.linalg.norm(X_train, axis=1) * np.linalg.norm(X_test_sample)
            cos_sim = np.dot(X_train, X_test_sample.T) / norms
            return 1 - cos_sim
        else:
            return np.sqrt(((X_train - X_test_sample) ** 2).sum(axis=1))
    predictions = []
    for test_sample in X_test:
        distances = compute_distances(X_train, test_sample, metric)
        k_indices = np.argsort(distances)[:k]
        k_nearest_labels = y_train[k_indices]
        (values, counts) = np.unique(k_nearest_labels, return_counts=True)
        majority_index = np.argmax(counts)
        predictions.append(values[majority_index])
    return np.array(predictions)

