def metric(name: str):
    """Factory producing a validated distance function.
    Please refer to the docstring in the question for details.
    """

    def is_valid_vector(vec):
        if not isinstance(vec, (list, tuple, np.ndarray)):
            return False
        if isinstance(vec, np.ndarray) and vec.ndim != 1:
            return False
        if len(vec) == 0:
            return False
        try:
            vec = np.array(vec, dtype=float)
        except ValueError:
            return False
        return True

    def euclidean(vec1, vec2):
        return np.round(np.linalg.norm(vec1 - vec2), 4)

    def manhattan(vec1, vec2):
        return np.round(np.linalg.norm(vec1 - vec2, ord=1), 4)

    def chebyshev(vec1, vec2):
        return np.round(np.linalg.norm(vec1 - vec2, ord=np.inf), 4)

    def cosine(vec1, vec2):
        norm1 = np.linalg.norm(vec1)
        norm2 = np.linalg.norm(vec2)
        if norm1 == 0 or norm2 == 0:
            return -1
        return np.round(1 - np.dot(vec1, vec2) / (norm1 * norm2), 4)

    def distance(vec1, vec2, func):
        if not (is_valid_vector(vec1) and is_valid_vector(vec2)):
            return -1
        (vec1, vec2) = (np.array(vec1, dtype=float), np.array(vec2, dtype=float))
        if len(vec1) != len(vec2):
            return -1
        return func(vec1, vec2)
    if name == 'euclidean':
        return lambda vec1, vec2: distance(vec1, vec2, euclidean)
    elif name == 'manhattan':
        return lambda vec1, vec2: distance(vec1, vec2, manhattan)
    elif name == 'chebyshev':
        return lambda vec1, vec2: distance(vec1, vec2, chebyshev)
    elif name == 'cosine':
        return lambda vec1, vec2: distance(vec1, vec2, cosine)
    else:
        return lambda vec1, vec2: -1

def calculate_hand_score(hand: list[int]) -> int:
    """Calculate the score of the hand using Blackjack rules including usable ace."""
    score = sum(hand)
    aces = hand.count(1)
    while score + 10 <= 21 and aces > 0:
        score += 10
        aces -= 1
    return score
def is_natural_blackjack(hand: list[int]) -> bool:
    """Check if the hand is a natural blackjack (one Ace and one 10-value card)."""
    return len(hand) == 2 and 1 in hand and (10 in hand)
def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """
    player_score = calculate_hand_score(player)
    dealer_score = calculate_hand_score(dealer)
    if player_score > 21:
        return -1.0
    elif dealer_score > 21:
        return 1.0
    elif player_score > dealer_score:
        return 1.5 if is_natural_blackjack(player) else 1.0
    elif player_score == dealer_score:
        return 0.0 if not is_natural_blackjack(player) else 1.0
    else:
        return -1.0

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """

    def _leaky_relu_helper(x):
        return np.where(x >= 0, x, a * x)
    if isinstance(z, (int, float)):
        return float(_leaky_relu_helper(z))
    elif isinstance(z, list):
        return [_leaky_relu_helper(item).tolist() if isinstance(item, np.ndarray) else leaky_relu(item, a) for item in z]
    elif isinstance(z, np.ndarray):
        return _leaky_relu_helper(z).tolist()
    else:
        raise TypeError('Unsupported input type, must be int, float, list, or numpy.ndarray.')

