                    (h_start, w_start) = (i * s_h, j * s_w)
                    (h_end, w_end) = (h_start + p_h, w_start + p_w)
                    output[n, c, i, j] = np.mean(X[n, c, h_start:h_end, w_start:w_end])
    for n in range(batch_size):
        for c in range(channels):
            for i in range(o_h):
                for j in range(o_w):
                    (h_start, w_start) = (i * s_h, j * s_w)
                    (h_end, w_end) = (h_start + p_h, w_start + p_w)
                    grad_input[n, c, h_start:h_end, w_start:w_end] += accum_grad[n, c, i, j] / (p_h * p_w)
    output = np.round(output, 4).tolist()
    grad_input = np.round(grad_input, 4).tolist()
    return (output, grad_input)

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(np.array(tree_a.value), np.array(tree_b.value))
    elif isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature != tree_b.feature or not np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    else:
        return False

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs / 2

    def hz_to_mel(f):
        return 2595 * np.log10(1 + f / 700)

    def mel_to_hz(mel):
        return 700 * (10 ** (mel / 2595) - 1)
    mel_min = hz_to_mel(min_freq)
    mel_max = hz_to_mel(max_freq)
    mels = np.linspace(mel_min, mel_max, n_filters + 2)
    freqs = mel_to_hz(mels)
    dft_bins = np.linspace(0, fs / 2, N // 2 + 1)
    filterbank = np.zeros((n_filters, N // 2 + 1))
    for i in range(n_filters):
        left = freqs[i]
        center = freqs[i + 1]
        right = freqs[i + 2]
        left_idx = np.searchsorted(dft_bins, left)
        center_idx = np.searchsorted(dft_bins, center)
        right_idx = np.searchsorted(dft_bins, right)
        for k in range(N // 2 + 1):
            if k > left_idx and k < center_idx:
                filterbank[i, k] = (k - left_idx) / (center_idx - left_idx)
            elif k > center_idx and k < right_idx:
                filterbank[i, k] = (right_idx - k) / (right_idx - center_idx)
    if normalize:
        for i in range(n_filters):
            filterbank[i] *= 2 / (freqs[i + 2] - freqs[i])
    filterbank = filterbank.tolist()
    for (i, row) in enumerate(filterbank):
        filterbank[i] = [round(x, 4) for x in row]
    return filterbank

import numpy as np
def conv2D(X: 'np.ndarray', W: 'np.ndarray', stride: int, pad, dilation: int=0):
    """Performs a 2-D convolution (cross-correlation).

    Args:
        X: NumPy array of shape ``(n_ex, in_rows, in_cols, in_ch)`` representing the input batch.
        W: NumPy array of shape ``(kernel_rows, kernel_cols, in_ch, out_ch)`` containing the kernels.
        stride: Stride of the convolution.
        pad: Padding specification – integer, tuple or the string ``'same'``.
        dilation: Optional dilation factor. ``0`` corresponds to a normal convolution.

    Returns:
        The convolution result **as a Python list** obtained through ``numpy.ndarray.tolist()``.
    """
    (n_ex, in_rows, in_cols, in_ch) = X.shape
    (kr, kc, _, out_ch) = W.shape
    if dilation > 0:
        kr = 1 + dilation * (kr - 1)
        kc = 1 + dilation * (kc - 1)
    if type(pad) == int:
        (pr1, pr2, pc1, pc2) = (pad, pad, pad, pad)
    elif len(pad) == 2:
        (pr1, pr2, pc1, pc2) = (pad[0], pad[0], pad[1], pad[1])
    elif len(pad) == 4:
        (pr1, pr2, pc1, pc2) = pad
    elif pad == 'same':
        (pr1, pr2) = ((kr - 1) // 2, kr // 2)
        (pc1, pc2) = ((kc - 1) // 2, kc // 2)
    else:
        raise ValueError('Invalid padding specification')
    X_padded = np.pad(X, ((0, 0), (pr1, pr2), (pc1, pc2), (0, 0)), mode='constant', constant_values=0)
    out_rows = (in_rows + pr1 + pr2 - kr) // stride + 1
    out_cols = (in_cols + pc1 + pc2 - kc) // stride + 1
    Y = np.zeros((n_ex, out_rows, out_cols, out_ch))
    for i in range(out_rows):
        for j in range(out_cols):
            for k in range(out_ch):
                window = X_padded[:, i * stride:i * stride + kr, j * stride:j * stride + kc, :]
                W_dilated = np.zeros((kr, kc, in_ch))
                if dilation > 0:
                    for mi in range(kr):
                        for mj in range(kc):
                            if mi % (dilation + 1) == 0 and mj % (dilation + 1) == 0:
                                W_dilated[mi, mj, :] = W[mi // (dilation + 1), mj // (dilation + 1), :, k]
                else:
                    W_dilated = W[:, :, :, k]
                Y[:, i, j, k] = np.sum(window * W_dilated, axis=(1, 2, 3))
    return Y.tolist()

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_continuous(space: Space) -> bool:
        """Check if a space or all subspaces are Box."""
        if isinstance(space, (Tuple, Dict)):
            subspaces = space.spaces.values() if isinstance(space, Dict) else space.spaces
            return all((isinstance(sub, Box) for sub in subspaces))
        return isinstance(space, Box)
    cont_action = check_continuous(env.action_space) if tuple_action else isinstance(env.action_space, Box)
    cont_obs = check_continuous(env.observation_space) if tuple_obs else isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    def mse(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    def split_mse(data, val):
        (left, right) = (data[data[:, 0] <= val][:, 1:], data[data[:, 0] > val][:, 1:])
        mse_left = mse(left[:, 0], np.mean(left[:, 0])) if len(left) > 0 else 0
        mse_right = mse(right[:, 0], np.mean(right[:, 0])) if len(right) > 0 else 0
        return (mse_left + mse_right, (np.mean(left[:, 0]) if len(left) > 0 else 0, np.mean(right[:, 0]) if len(right) > 0 else 0))

    def find_best_split(data):
        best_split = (None, float('inf'), None, None)
        for i in range(data.shape[1] - 1):
            for val in np.unique(data[:, i]):
                (mse_val, (left_pred, right_pred)) = split_mse(np.hstack((data[:, [i]], data[:, -1:])), val)
                if mse_val < best_split[2]:
                    best_split = (i, val, mse_val, (left_pred, right_pred))
        return best_split

    def build_tree(data, depth):
        if depth == max_depth or len(data) <= 1:
            return np.mean(data[:, -1])
        best_split = find_best_split(data)
        left = data[data[:, best_split[0]] <= best_split[1]][:, -1]
        right = data[data[:, best_split[0]] > best_split[1]][:, -1]
        return {'feature': best_split[0], 'value': best_split[1], 'left': build_tree(left, depth + 1), 'right': build_tree(right, depth + 1)}

    def predict_tree(tree, x):
        if isinstance(tree, np.ndarray) or isinstance(tree, float):
            return tree
        if x[tree['feature']] <= tree['value']:
            return predict_tree(tree['left'], x)
        return predict_tree(tree['right'], x)
    predictions = np.full(len(y_train), np.mean(y_train))
    for _ in range(n_estimators):
        residuals = y_train - predictions
        tree = build_tree(np.hstack((X_train, residuals[:, None])), 0)
        predictions += learning_rate * np.array([predict_tree(tree, x) for x in X_train])
    final_predictions = np.mean(y_train) + np.array([sum((learning_rate * predict_tree(build_tree(np.hstack((X_train, (y_train - np.mean(y_train))[:, None])), 0), x) for _ in range(n_estimators))) for x in X_test])
    return [round(float(pred), 4) for pred in final_predictions.tolist()]
