import itertools
def construct_tree(transactions, min_sup):
    item_counts = Counter((item for transaction in transactions for item in transaction))
    header = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    header = dict(sorted(header.items(), key=lambda x: (-x[1], x[0])))
    fp_tree = FPNode(None)
    for transaction in transactions:
        filtered_trans = sorted((item for item in transaction if item in header), key=lambda x: (-header[x], x))
        if filtered_trans:
            fp_tree.insert(filtered_trans)
    return (fp_tree, header)
def find_frequent_itemsets(fp_tree, header):

    def mine_tree(tree, prefix, header, item_sets):
        if tree.is_single_path():
            for (item, count) in tree.yield_path():
                yield (tuple(sorted(prefix + (item,))), count)
                for (path_item, path_count) in tree.yield_path():
                    if path_item != item:
                        yield (tuple(sorted(prefix + (item, path_item))), min(path_count, count))
        else:
            for item in header:
                new_prefix = prefix + (item,)
                yield (new_prefix, header[item])
                (conditional_tree, _) = construct_tree(tree.get_conditional_patterns(item), 0)
                for (pattern, count) in mine_tree(conditional_tree, new_prefix, conditional_tree.header, item_sets):
                    yield (pattern, min(count, header[item]))
    all_item_sets = set()
    for (item_set, _) in mine_tree(fp_tree, (), header, all_item_sets):
        all_item_sets.add(item_set)
    return all_item_sets
class FPNode:

    def __init__(self, item):
        self.item = item
        self.count = 0
        self.children = {}
        self.parent = None
        self.next_node = None

    def insert(self, transaction):
        self.count += 1
        if not transaction:
            return
        (next_item, *remaining_items) = transaction
        if next_item in self.children:
            next_node = self.children[next_item]
        else:
            next_node = FPNode(next_item)
            next_node.parent = self
            self.children[next_item] = next_node
        next_node.insert(remaining_items)

    def is_single_path(self):
        if not self.children or self.count <= 1:
            return True
        for child in self.children.values():
            if not child.is_single_path():
                return False
        return True

    def yield_path(self):
        for child in self.children.values():
            yield from child.yield_path()
        yield (self.item, self.count)

    def get_conditional_patterns(self, item):

        def traverse(node, path):
            if node.item == item:
                yield path[::-1]
            else:
                for child in node.children.values():
                    yield from traverse(child, path + [child.item])
        return [path for path in traverse(self, [])]
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    (fp_tree, header) = construct_tree(transactions, min_sup)
    frequent_item_sets = find_frequent_itemsets(fp_tree, header)
    return sorted(frequent_item_sets, key=lambda x: (len(x), x))

import numpy as np
def sigmoid(z):
    """Compute the numerically stable, element-wise sigmoid activation.

    The function must work for a scalar, list/tuple, or NumPy ndarray input.

    Args:
        z: int, float, list, tuple, or numpy.ndarray – input values.

    Returns:
        float if *z* is a scalar, otherwise a Python list obtained with
        ``numpy.ndarray.tolist()``, where every element is the sigmoid of the
        corresponding input value and is rounded to four decimal places.
    """
    if isinstance(z, (list, tuple)):
        z = np.array(z)
    elif isinstance(z, np.ndarray):
        pass
    else:
        return round(1 / (1 + np.exp(-z)), 4)
    sig = np.where(z >= 0, 1 / (1 + np.exp(-z)), np.exp(z) / (1 + np.exp(z)))
    sig_rounded = np.around(sig, decimals=4)
    if isinstance(z, np.ndarray):
        if z.size == 1:
            return float(sig_rounded[0])
        else:
            return sig_rounded.tolist()
    else:
        raise TypeError('Input must be int, float, list, tuple, or numpy.ndarray')

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, (float, int)):
        return np.round(z * (1 - z), 4)
    elif isinstance(z, list) and len(z) == 0:
        return []
    elif isinstance(z, list):
        return [sigmoid_grad(item) for item in z]
    elif isinstance(z, np.ndarray):
        result = z * (1 - z)
        result = np.round(result, 4)
        if z.shape == ():
            return float(result)
        return result.tolist()
    else:
        raise ValueError('Unsupported input type. Please use a float, list, or NumPy ndarray.')

