def compute_cost(AL: np.ndarray, Y: np.ndarray) -> float:
    m = Y.shape[1] if Y.ndim == 2 else Y.size
    AL = np.clip(AL, 1e-15, 1 - 1e-15)
    cost = -1 / m * np.sum(Y * np.log(AL) + (1 - Y) * np.log(1 - AL))
    return float(np.squeeze(cost))

import numpy as np
def gaussian_kernel(x, y, gamma):
    return np.exp(-gamma * np.sum((x - y) ** 2))
def pairwise_similarities(points, gamma):
    N = points.shape[0]
    W = np.zeros((N, N))
    for i in range(N):
        for j in range(i, N):
            W[i, j] = W[j, i] = gaussian_kernel(points[i], points[j], gamma)
    return W
def laplacian(W, method='unnormalized'):
    D = np.diag(np.sum(W, axis=1))
    if method == 'unnormalized':
        L = D - W
    else:
        D_inv_sqrt = np.linalg.inv(np.sqrt(D))
        L = np.dot(np.dot(D_inv_sqrt, W), D_inv_sqrt)
    return L
def kmeans(X, k):
    centroids = X[:k].copy()
    prev_labels = np.zeros(X.shape[0])
    while True:
        dists = np.linalg.norm(X[:, np.newaxis] - centroids, axis=2)
        labels = np.argmin(dists, axis=1)
        if np.array_equal(labels, prev_labels):
            break
        prev_labels = labels
        for i in range(k):
            if np.any(labels == i):
                centroids[i] = np.mean(X[labels == i], axis=0)
    return labels
def spectral_clustering(data: np.ndarray, n_cluster: int, gamma: float=2.0, method: str='unnormalized') -> list[int]:
    N = data.shape[0]
    if n_cluster == 1:
        return [0] * N
    W = pairwise_similarities(data, gamma)
    L = laplacian(W, method=method)
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    U = eigenvectors[:, :n_cluster]
    if method == 'normalized':
        row_norms = np.linalg.norm(U, axis=1, keepdims=True)
        U = U / row_norms
    cluster_labels = kmeans(U, n_cluster)
    sorted_labels = np.argsort(np.argmin(cluster_labels))
    sorted_labels_to_original = {i: np.argmin(cluster_labels == i) for i in range(n_cluster)}
    final_labels = [sorted_labels_to_original[label] for label in cluster_labels]
    return final_labels

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import numpy as np
import math
from typing import List
def average_path_length(n: int) -> float:
    """Calculate the average path length for a binary tree."""
    if n <= 1:
        return 0.0
    return 2.0 * math.log(n - 1) + 0.5772156649015329 - 2.0 * (n - 1) / n
def build_tree(data: np.ndarray, height_limit: int, depth: int=0) -> dict:
    """Build a randomly split isolation tree."""
    if data.shape[0] <= 1 or depth >= height_limit:
        return {'c': data.shape[0]}
    feature = np.random.randint(data.shape[1])
    (min_val, max_val) = (data[:, feature].min(), data[:, feature].max())
    if min_val == max_val:
        threshold = min_val
    else:
        threshold = np.random.uniform(min_val, max_val)
    left = data[data[:, feature] <= threshold]
    right = data[data[:, feature] > threshold]
    return {'feature': feature, 'threshold': threshold, 'left': build_tree(left, height_limit, depth + 1), 'right': build_tree(right, height_limit, depth + 1)}
def path_length(tree: dict, x: np.ndarray) -> float:
    """Calculate path length of x in the isolation tree."""
    while True:
        if 'c' in tree:
            return tree['c']
        if x[tree['feature']] <= tree['threshold']:
            tree = tree['left']
        else:
            tree = tree['right']
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> List[int]:
    """Tiny Isolation Forest implementation."""
    n_samples = data.shape[0]
    if sample_size >= n_samples:
        sample_indices = np.arange(n_samples)
    else:
        sample_indices = np.random.choice(n_samples, sample_size, replace=False)
    trees = []
    height_limit = math.ceil(math.log2(sample_size))
    for _ in range(n_trees):
        sample_data = data[sample_indices]
        trees.append(build_tree(sample_data, height_limit))
    path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        for tree in trees:
            path_lengths[i] += path_length(tree, data[i])
    path_lengths /= n_trees
    c_n = average_path_length(sample_size)
    anomaly_scores = 2.0 ** (-path_lengths / c_n)
    threshold = np.sort(anomaly_scores)[int((1 - epsilon) * n_samples)]
    outlier_indices = np.where(anomaly_scores >= threshold)[0].tolist()
    return sorted(outlier_indices)

import numpy as np
def euclidean_distance(point1, point2):
    """Compute the Euclidean distance between two points."""
    return np.sqrt(np.sum((point1 - point2) ** 2))
def pairwise_euclidean_distances(data):
    """Compute the Euclidean distance matrix for a dataset."""
    n_samples = data.shape[0]
    dist_matrix = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(i, n_samples):
            dist = euclidean_distance(data[i], data[j])
            dist_matrix[i][j] = dist
            dist_matrix[j][i] = dist
    return dist_matrix
def k_distance_and_nearest_neighbors(dist_matrix, k):
    """Find the k-distance and indexes of the k nearest neighbors for each point."""
    k_distances = []
    neighbors = []
    for i in range(dist_matrix.shape[0]):
        sorted_indices = np.argsort(dist_matrix[i])
        k_nearest_indices = sorted_indices[1:k + 1]
        k_dist = dist_matrix[i][k_nearest_indices[-1]]
        k_distances.append(k_dist)
        neighbors.append(k_nearest_indices)
    return (k_distances, neighbors)
def reachability_distance(dist_matrix, k_distances, point1, point2):
    """Calculate reachability distance."""
    return max(k_distances[point2], dist_matrix[point1][point2])
def local_reachability_density(dist_matrix, k_distances, neighbors, point):
    """Calculate Local Reachability Density for a given point."""
    reach_dists = [reachability_distance(dist_matrix, k_distances, point, neighbor) for neighbor in neighbors[point]]
    return len(neighbors[point]) / np.sum(reach_dists)
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor (LOF) score for each point in the dataset."""
    dist_matrix = pairwise_euclidean_distances(data)
    (k_distances, neighbors) = k_distance_and_nearest_neighbors(dist_matrix, k)
    lrd = [local_reachability_density(dist_matrix, k_distances, neighbors, point) for point in range(data.shape[0])]
    lof_scores = []
    for point in range(data.shape[0]):
        lrd_nk_sum = np.sum([lrd[neighbor] for neighbor in neighbors[point]])
        lof = lrd_nk_sum / k / lrd[point]
        lof_scores.append(lof)
    lof_scores = np.round(lof_scores, decimals=4)
    outlier_indices = [i for (i, score) in enumerate(lof_scores) if score > epsilon]
    return (lof_scores, sorted(outlier_indices))
