import numpy as np
import numpy as np
def gini_impurity(y):
    """Compute the Gini impurity of a set of labels."""
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def split_gain(X, y, feature_idx, threshold):
    """Calculate Gini-gain from performing a split."""
    left_mask = X[:, feature_idx] < threshold
    left_y = y[left_mask]
    right_y = y[~left_mask]
    gain = gini_impurity(y) - (len(left_y) / len(y) * gini_impurity(left_y) + len(right_y) / len(y) * gini_impurity(right_y))
    return (gain, left_mask)
def best_split(X, y):
    """Find the best split (feature, threshold) with the highest Gini-gain."""
    (best_feature, best_threshold, best_gain) = (-1, -1, -1)
    for feature_idx in range(X.shape[1]):
        unique_values = np.unique(X[:, feature_idx])
        midpoints = (unique_values[:-1] + unique_values[1:]) / 2
        for threshold in midpoints:
            (gain, _) = split_gain(X, y, feature_idx, threshold)
            if gain > best_gain:
                best_gain = gain
                best_feature = feature_idx
                best_threshold = threshold
    return (best_feature, best_threshold, best_gain)
def node_majority_vote(y):
    """Determine the majority class label."""
    (values, counts) = np.unique(y, return_counts=True)
    return values[np.argmax(counts)]
def recursive_split(X, y, depth=0, max_depth=None):
    """Construct the decision tree recursively."""
    if len(np.unique(y)) == 1:
        return {'label': y[0]}
    if max_depth is not None and depth >= max_depth:
        return {'label': node_majority_vote(y)}
    (feature, threshold, gain) = best_split(X, y)
    if gain <= 0:
        return {'label': node_majority_vote(y)}
    left_mask = X[:, feature] < threshold
    right_mask = ~left_mask
    if not np.any(left_mask) or not np.any(right_mask):
        return {'label': node_majority_vote(y)}
    return {'feature': feature, 'threshold': threshold, 'left': recursive_split(X[left_mask], y[left_mask], depth + 1, max_depth), 'right': recursive_split(X[right_mask], y[right_mask], depth + 1, max_depth)}
def classify(tree, x):
    """Classify a single instance using the decision tree."""
    if 'label' in tree:
        return tree['label']
    if x[tree['feature']] < tree['threshold']:
        return classify(tree['left'], x)
    else:
        return classify(tree['right'], x)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    tree = recursive_split(X_train, y_train, max_depth=max_depth)
    return [classify(tree, x) for x in X_test]
