import numpy as np
def metric(name: str):

    def validate_and_calculate(x, y, mode):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except ValueError:
            return -1
        if x.ndim != 1 or y.ndim != 1 or len(x) != len(y) or (not len(x)):
            return -1
        if mode == 'euclidean':
            return np.round(np.sqrt(np.sum((x - y) ** 2)), 4)
        elif mode == 'manhattan':
            return np.round(np.sum(np.abs(x - y)), 4)
        elif mode == 'chebyshev':
            return np.round(np.max(np.abs(x - y)), 4)
        elif mode == 'cosine':
            if np.linalg.norm(x) == 0 or np.linalg.norm(y) == 0:
                return -1
            return np.round(1 - np.dot(x, y) / (np.linalg.norm(x) * np.linalg.norm(y)), 4)
        return -1
    if name.lower() in ['euclidean', 'manhattan', 'chebyshev', 'cosine']:
        return lambda x, y: validate_and_calculate(x, y, name.lower())
    else:
        return lambda x, y: -1
