def calculate_score(hand: list[int]) -> int:
    """Calculate the score for one hand."""
    ace_count = hand.count(1)
    score = sum((card for card in hand if card != 1))
    for _ in range(ace_count):
        if score + 11 <= 21:
            score += 11
        else:
            score += 1
    return score
def is_natural_blackjack(hand: list[int]) -> bool:
    """Check if the hand has a natural blackjack (an Ace and a 10-value card)."""
    return sorted(hand) == [1, 10]
def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack."""
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score > 21:
        return -1
    if dealer_score > 21:
        return 1
    if is_natural_blackjack(player) and len(player) == 2:
        return 1.5
    elif player_score > dealer_score:
        return 1
    elif player_score < dealer_score:
        return -1
    else:
        return 0
