def mse(y_true, y_pred):
    return np.mean((y_true - y_pred) ** 2)
def split_node(X, y, depth, max_depth):
    if depth >= max_depth:
        return (np.mean(y), None, None, None, None)
    best_mse = np.inf
    best_feature_index = None
    best_threshold = None
    (left_indices, right_indices) = (None, None)
    for feature_index in range(X.shape[1]):
        thresholds = np.unique(X[:, feature_index])
        for threshold in thresholds:
            left_mask = X[:, feature_index] < threshold
            y_left = y[left_mask]
            y_right = y[~left_mask]
            if len(y_left) == 0 or len(y_right) == 0:
                continue
            mse_left = mse(y_left, np.mean(y_left))
            mse_right = mse(y_right, np.mean(y_right))
            total_mse = mse_left + mse_right
            if total_mse < best_mse:
                best_mse = total_mse
                best_feature_index = feature_index
                best_threshold = threshold
                left_indices = np.where(left_mask)[0]
                right_indices = np.where(~left_mask)[0]
    if best_feature_index is not None and best_threshold is not None:
        left_value = split_node(X[left_indices], y[left_indices], depth + 1, max_depth)
        right_value = split_node(X[right_indices], y[right_indices], depth + 1, max_depth)
        return (best_feature_index, best_threshold, left_value, right_value)
    else:
        return (np.mean(y), None, None, None)
