import numpy as np
from collections import defaultdict
def _mean_center(vector):
    """Mean center the vector if its standard deviation is larger than 1e-3."""
    sd = np.std(vector)
    if sd > 0.001:
        return vector - np.mean(vector)
    return vector
def _cosine_similarity(v1, v2):
    """Compute the cosine similarity between two vectors."""
    return np.dot(v1, v2) / (np.linalg.norm(v1) * np.linalg.norm(v2))
def _pearson_similarity(v1, v2):
    """Compute the Pearson correlation coefficient between two vectors."""
    return np.corrcoef(v1, v2)[0, 1]
def knn_recommend(data: np.ndarray, user_ind: int, k: int, criterion: str='cosine') -> list[int]:
    """Item-based k-NN collaborative filtering recommender."""
    (n_users, n_items) = data.shape
    S = np.zeros((n_items, n_items))
    for i in range(n_items):
        for j in range(i, n_items):
            common_users = np.where((data[:, i] > 0) & (data[:, j] > 0))[0]
            if len(common_users) == 0:
                S[i, j] = S[j, i] = 0
            else:
                v1 = data[common_users, i]
                v2 = data[common_users, j]
                if criterion == 'cosine':
                    v1 = _mean_center(v1)
                    v2 = _mean_center(v2)
                    S[i, j] = S[j, i] = _cosine_similarity(v1, v2)
                elif criterion == 'pearson':
                    S[i, j] = S[j, i] = _pearson_similarity(v1, v2)
    rated_indices = np.where(data[user_ind] > 0)[0]
    if len(rated_indices) == n_items:
        return []
    scores = defaultdict(float)
    for t in set(range(n_items)) - set(rated_indices):
        rated_similarities = S[t, rated_indices]
        scores[t] = np.sum(data[user_ind, rated_indices] * rated_similarities) / max(np.sum(np.abs(rated_similarities)), 1e-10)
    recommended_items = sorted(scores.items(), key=lambda x: x[1], reverse=True)
    return [item[0] for item in recommended_items[:k]]
