from typing import List
def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """

    def build_tree(items, header_table):
        root = Node(None, 1, None)
        for transaction in items:
            updated_transaction = [item for item in transaction if item in header_table]
            updated_transaction.sort(key=lambda x: header_table[x][0][0], reverse=True)
            curr_node = root
            for item in updated_transaction:
                if item in curr_node.children:
                    curr_node.children[item].count += 1
                else:
                    new_node = Node(item, 1, curr_node)
                    curr_node.children[item] = new_node
                    if header_table[item][0] is not None:
                        n = header_table[item][0]
                        while n.next is not None:
                            n = n.next
                        n.next = new_node
                    else:
                        header_table[item][0] = new_node
                curr_node = curr_node.children[item]
        return root

    def create_cond_pattern_base(header_table, path):
        nodes = header_table[path][0]
        new_patterns = []
        while nodes:
            p = []
            n = nodes
            while n.parent is not None and n.parent.item is not None:
                p.insert(0, n.parent.item)
                n = n.parent
            if len(p) > 1:
                new_patterns.append((p.copy(), nodes.count))
            nodes = nodes.next
        return new_patterns

    def mine_patterns(header_table, prefix, patterns):
        sorted_header = [item[0] for item in sorted(header_table.items(), key=lambda x: x[1][0][0])]
        for item in reversed(sorted_header):
            new_patterns = create_cond_pattern_base(header_table, item)
            if len(new_patterns) > 0:
                condensed_patterns = condense_patterns(new_patterns)
                if condensed_patterns:
                    suffix = prefix + [item]
                    patterns.append(suffix)
                    (new_header, new_root) = build_tree(condensed_patterns, defaultdict(list))
                    if new_header:
                        mine_patterns(new_header, suffix, patterns)

    def condense_patterns(patterns):
        condensed = Counter()
        for (pattern, count) in patterns:
            pattern = tuple(pattern)
            if pattern:
                condensed[pattern] += count
        return [list(k) for (k, v) in condensed.items() if v >= min_support]
    global_frequency = Counter()
    for transaction in transactions:
        global_frequency.update(transaction)
    items = {k: v for (k, v) in global_frequency.items() if v >= min_support}
    items = {item: (count, None) for (item, count) in sorted(items.items(), key=lambda x: -x[1])}
    if not items:
        return []
    root = build_tree(transactions, items)
    header_table = items
    patterns = []
    mine_patterns(header_table, [], patterns)
    patterns.extend([[item] for item in items])
    patterns.sort(key=lambda x: (len(x), x))
    return patterns
