import random
def kmeans(X: np.ndarray, K: int, max_iter: int=100, random_state: int | None=None) -> list[list[float]]:
    """Perform K-Means clustering with K-Means++ initialisation."""

    def kmeans_plus_plus(X, K, random_state):
        np.random.seed(random_state)
        random.seed(random_state)
        centroids = [X[np.random.choice(X.shape[0]), :]]
        for _ in range(1, K):
            dist_sq = np.array([min([np.inner(c - x, c - x) for c in centroids]) for x in X])
            probs = dist_sq / dist_sq.sum()
            cumprobs = probs.cumsum()
            r = random.random()
            for (j, p) in enumerate(cumprobs):
                if r < p:
                    i = j
                    break
            centroids.append(X[i])
        return np.array(centroids)

    def assign_clusters(X, centroids):
        dist = np.sqrt(((X[:, None] - centroids) ** 2).sum(axis=-1))
        return np.argmin(dist, axis=1)

    def update_centroids(X, labels, K):
        new_centroids = np.array([X[labels == k].mean(axis=0) if np.any(labels == k) else centroids[k] for k in range(K)])
        return new_centroids
    np.random.seed(random_state)
    random.seed(random_state)
    centroids = kmeans_plus_plus(X, K, random_state)
    for _ in range(max_iter):
        old_centroids = centroids
        labels = assign_clusters(X, old_centroids)
        centroids = update_centroids(X, labels, K)
        if np.array_equal(centroids, old_centroids):
            break
    return [list(np.round(c, 4)) for c in sorted(centroids, key=lambda x: (x[0], tuple(x)))]

import numpy as np
TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Encodes a string into a fixed-length list of integer token ids."""
    encoded = [0] * time_steps
    for (i, char) in enumerate(text):
        if i == time_steps:
            break
        encoded[i] = vocabulary.get(char, 0)
    return encoded
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Decodes a list of integer ids back into text, ignoring padding tokens."""
    return ''.join((inverse_vocab[i] for i in indices if i != 0))
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Encodes text, predicts using the model, decodes the result."""
    encoded_text = np.array([string_to_int(text, TIME_STEPS, input_vocabulary)])
    prediction = model.predict(encoded_text)[0]
    predicted_ids = np.argmax(prediction, axis=-1)
    decoded_text = int_to_string(predicted_ids, inv_output_vocabulary)
    return decoded_text

