import math
def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire.

    Parameters
    ----------
    grid : list[str]
        Square maze represented as a list of strings. Each character must be
        '.', '#', or 'F'.  The agent starts at the upper-left corner (0,0) and
        wishes to reach the lower-right corner (n-1,n-1).  Fire starts in every
        cell marked with 'F' and spreads to the four neighbouring cells every
        minute.  The fire spreads first, then the agent moves.

    Returns
    -------
    int
        Minimum number of minutes required for the agent to reach the goal
        without entering a burning cell, or -1 if this is impossible.
    """
    n = len(grid)
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
    (fire_queue, agent_queue) = (deque(), deque())
    for r in range(n):
        for c in range(n):
            if grid[r][c] == 'F':
                fire_queue.append((r, c, 0))
            elif r == c == 0:
                if grid[r][c] != 'F':
                    agent_queue.append((r, c, 0))
    if grid[0][0] == 'F' or grid[-1][-1] == 'F':
        return -1
    fire_spread = [[float('inf')] * n for _ in range(n)]
    while fire_queue:
        (r, c, time) = fire_queue.popleft()
        if fire_spread[r][c] > time:
            fire_spread[r][c] = time
            for (dr, dc) in directions:
                (nr, nc) = (r + dr, c + dc)
                if 0 <= nr < n and 0 <= nc < n and (grid[nr][nc] == '.'):
                    fire_queue.append((nr, nc, time + 1))
                    grid[nr][nc] = '#'
    visited = set()
    while agent_queue:
        (r, c, time) = agent_queue.popleft()
        if (r, c) == (n - 1, n - 1):
            return time
        if (r, c) in visited:
            continue
        visited.add((r, c))
        for (dr, dc) in directions:
            (nr, nc) = (r + dr, c + dc)
            if 0 <= nr < n and 0 <= nc < n and (grid[nr][nc] == '.') and (time + 1 < fire_spread[nr][nc]):
                agent_queue.append((nr, nc, time + 1))
    return -1

import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement a simple k-Nearest Neighbors classifier."""
    if metric == 'manhattan':
        dist_func = lambda x, y: np.abs(x - y).sum(axis=1)
    elif metric == 'cosine':

        def dist_func(x, y):
            norms = np.linalg.norm(x, axis=1) * np.linalg.norm(y[0], axis=0)
            dot_products = np.dot(x, y[0])
            return 1 - dot_products / norms
    else:
        dist_func = lambda x, y: np.sqrt(((x - y) ** 2).sum(axis=1))
    predictions = []
    for test_sample in X_test:
        distances = dist_func(X_train, test_sample.reshape(1, -1))
        k_nearest_indices = np.argsort(distances)[:k]
        k_nearest_labels = y_train[k_nearest_indices]
        (values, counts) = np.unique(k_nearest_labels, return_counts=True)
        index = np.argmax(counts)
        predicted_label = values[index]
        predictions.append(predicted_label)
    return np.array(predictions)

import numpy as np
def gini_impurity(y):
    """Compute the Gini impurity of a set of labels."""
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def split_gain(X, y, feature_idx, threshold):
    """Calculate Gini-gain from performing a split."""
    left_mask = X[:, feature_idx] < threshold
    left_y = y[left_mask]
    right_y = y[~left_mask]
    gain = gini_impurity(y) - (len(left_y) / len(y) * gini_impurity(left_y) + len(right_y) / len(y) * gini_impurity(right_y))
    return (gain, left_mask)
def best_split(X, y):
    """Find the best split (feature, threshold) with the highest Gini-gain."""
    (best_feature, best_threshold, best_gain) = (-1, -1, -1)
    for feature_idx in range(X.shape[1]):
        unique_values = np.unique(X[:, feature_idx])
        midpoints = (unique_values[:-1] + unique_values[1:]) / 2
        for threshold in midpoints:
            (gain, _) = split_gain(X, y, feature_idx, threshold)
            if gain > best_gain:
                best_gain = gain
                best_feature = feature_idx
                best_threshold = threshold
    return (best_feature, best_threshold, best_gain)
def node_majority_vote(y):
    """Determine the majority class label."""
    (values, counts) = np.unique(y, return_counts=True)
    return values[np.argmax(counts)]
def recursive_split(X, y, depth=0, max_depth=None):
    """Construct the decision tree recursively."""
    if len(np.unique(y)) == 1:
        return {'label': y[0]}
    if max_depth is not None and depth >= max_depth:
        return {'label': node_majority_vote(y)}
    (feature, threshold, gain) = best_split(X, y)
    if gain <= 0:
        return {'label': node_majority_vote(y)}
    left_mask = X[:, feature] < threshold
    right_mask = ~left_mask
    if not np.any(left_mask) or not np.any(right_mask):
        return {'label': node_majority_vote(y)}
    return {'feature': feature, 'threshold': threshold, 'left': recursive_split(X[left_mask], y[left_mask], depth + 1, max_depth), 'right': recursive_split(X[right_mask], y[right_mask], depth + 1, max_depth)}
def classify(tree, x):
    """Classify a single instance using the decision tree."""
    if 'label' in tree:
        return tree['label']
    if x[tree['feature']] < tree['threshold']:
        return classify(tree['left'], x)
    else:
        return classify(tree['right'], x)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    tree = recursive_split(X_train, y_train, max_depth=max_depth)
    return [classify(tree, x) for x in X_test]

import numpy as np
def metric(name: str):

    def validate_and_calculate(x, y, mode):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except ValueError:
            return -1
        if x.ndim != 1 or y.ndim != 1 or len(x) != len(y) or (not len(x)):
            return -1
        if mode == 'euclidean':
            return np.round(np.sqrt(np.sum((x - y) ** 2)), 4)
        elif mode == 'manhattan':
            return np.round(np.sum(np.abs(x - y)), 4)
        elif mode == 'chebyshev':
            return np.round(np.max(np.abs(x - y)), 4)
        elif mode == 'cosine':
            if np.linalg.norm(x) == 0 or np.linalg.norm(y) == 0:
                return -1
            return np.round(1 - np.dot(x, y) / (np.linalg.norm(x) * np.linalg.norm(y)), 4)
        return -1
    if name.lower() in ['euclidean', 'manhattan', 'chebyshev', 'cosine']:
        return lambda x, y: validate_and_calculate(x, y, name.lower())
    else:
        return lambda x, y: -1

