import numpy as np
def hz_to_mel(frequencies):
    """Convert frequency in Hz to the Mel scale."""
    return 2595.0 * np.log10(1 + frequencies / 700.0)
def mel_to_hz(mels):
    """Convert frequency in Mel scale to Hz."""
    return 700.0 * (np.power(10, mels / 2595.0) - 1)
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix."""
    if max_freq is None:
        max_freq = fs // 2
    mel_min = hz_to_mel(min_freq)
    mel_max = hz_to_mel(max_freq)
    mels = np.linspace(mel_min, mel_max, n_filters + 2)
    freqs = mel_to_hz(mels)
    filterbank = []
    for i in range(n_filters):
        start = freqs[i]
        center = freqs[i + 1]
        end = freqs[i + 2]
        filter = np.zeros(N // 2 + 1)
        for k in range(N // 2 + 1):
            k_freq = k * fs / N
            if start < k_freq <= center:
                weight = (k_freq - start) / (center - start)
            elif center < k_freq <= end:
                weight = (end - k_freq) / (end - center)
            else:
                weight = 0.0
            filter[k] = max(0.0, weight)
        if normalize:
            filter *= 2 / (end - start)
        filterbank.append([round(freq, 4) for freq in filter.tolist()])
    return filterbank
