def euclidean_distance(point1, point2):
    """Calculate the Euclidean distance between two points."""
    return np.sqrt(np.sum((point1 - point2) ** 2))
def region_query(data, point_index, eps):
    """Find all points within the eps distance from a point."""
    neighbors = []
    for i in range(len(data)):
        if euclidean_distance(data[point_index], data[i]) <= eps:
            neighbors.append(i)
    return neighbors
def expand_cluster(data, labels, point_index, cluster_id, eps, min_samples):
    """Expand a new cluster from the point at point_index."""
    if labels[point_index] == -1:
        labels[point_index] = cluster_id
    elif labels[point_index] != 0:
        return
    labels[point_index] = cluster_id
    seeds = region_query(data, point_index, eps)
    if len(seeds) < min_samples:
        labels[point_index] = -1
        return
    for seed_index in seeds:
        if labels[seed_index] == -1:
            labels[seed_index] = cluster_id
        elif labels[seed_index] == 0:
            labels[seed_index] = cluster_id
            seed.neighbors = region_query(data, seed_index, eps)
            if len(seed.neighbors) >= min_samples:
                seeds += seed.neighbors
    return
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """Cluster *data* with the DBSCAN algorithm."""
    if data.size == 0:
        return []
    labels = [0 for _ in range(len(data))]
    cluster_id = 0
    for point_index in range(len(data)):
        if not labels[point_index]:
            neighbors = region_query(data, point_index, eps)
            if len(neighbors) < min_samples:
                labels[point_index] = -1
            else:
                cluster_id += 1
                expand_cluster(data, labels, point_index, cluster_id, eps, min_samples)
    return labels

