def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """

    def sse(data):
        """Calculate the Sum of Squared Errors."""
        return np.sum((data - np.mean(data)) ** 2)

    def find_best_split(X, y, current_depth):
        if current_depth == max_depth or len(y) < 2:
            return (np.mean(y), None, None, None)
        best_sse = float('inf')
        best_split = None
        best_left_indices = None
        best_right_indices = None
        for i in range(len(X) - 1):
            split = (X[i] + X[i + 1]) / 2
            left_indices = X <= split
            right_indices = ~left_indices
            if any(left_indices) and any(right_indices):
                left_sse = sse(y[left_indices])
                right_sse = sse(y[right_indices])
                total_sse = left_sse + right_sse
                if total_sse < best_sse:
                    best_sse = total_sse
                    best_split = split
                    best_left_indices = left_indices
                    best_right_indices = right_indices
        if best_split is None:
            return (np.mean(y), None, None, None)
        else:
            (left_values, left_split, _, _) = find_best_split(X[best_left_indices], y[best_left_indices], current_depth + 1)
            (right_values, right_split, _, _) = find_best_split(X[best_right_indices], y[best_right_indices], current_depth + 1)
            return (None, best_split, left_values, right_values)
    if not X_train:
        return []
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    sort_indices = X_train.argsort()
    X_train = X_train[sort_indices]
    y_train = y_train[sort_indices]
    (_, _, left_child, right_child) = find_best_split(X_train, y_train, 0)

    def predict_single(x, node):
        if isinstance(node, float):
            return node
        elif x < node:
            return predict_single(x, left_child)
        else:
            return predict_single(x, right_child)
    return [predict_single(x, right_child if x > left_child else left_child) for x in X_test]

