        tuple itself is sorted lexicographically.
    """

    class TreeNode:

        def __init__(self, name, count, parent):
            self.name = name
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None

        def increase(self, count):
            self.count += count

        def display(self, ind=1):
            print('  ' * ind, self.name, ' ', self.count)
            for child in self.children:
                self.children[child].display(ind + 1)

    def createFPtree(filtered_items, headerTable):
        rootNode = TreeNode('Null Set', 1, None)
        for transaction in filtered_items:
            updateTree(transaction, rootNode, headerTable, 1)
        return rootNode

    def updateTree(transaction, rootNode, headerTable, count):
        if transaction[0] in rootNode.children:
            rootNode.children[transaction[0]].increase(count)
        else:
            rootNode.children[transaction[0]] = TreeNode(transaction[0], count, rootNode)
            if headerTable[transaction[0]][1]:
                headerTable[transaction[0]][1].link = rootNode.children[transaction[0]]
            headerTable[transaction[0]][1] = rootNode.children[transaction[0]]
        if len(transaction) > 1:
            updateTree(transaction[1:], rootNode.children[transaction[0]], headerTable, count)

    def createHeaderTable(data, minSup=1):
        headerTable = {}
        for transaction in data:
            for item in transaction:
                headerTable[item] = headerTable.get(item, 0) + 1
        for k in list(headerTable):
            if headerTable[k] < minSup:
                del headerTable[k]
        freqItemSet = set(headerTable.keys())
        if len(freqItemSet) == 0:
            return (None, None)
        for k in headerTable:
            headerTable[k] = [headerTable[k], None]
        return headerTable

    def ascendFPtree(node, prefixPath):
        if node.parent is not None:
            prefixPath.append(node.name)
            ascendFPtree(node.parent, prefixPath)

    def findPrefixPath(treeNode):
        condPats = {}
        while treeNode is not None:
            prefixPath = []
            ascendFPtree(treeNode, prefixPath)
            if len(prefixPath) > 1:
                condPats[frozenset(prefixPath[:-1])] = treeNode.count
            treeNode = treeNode.link
        return condPats

    def mineTree(headerTable, prefix, freqItemSet, minSup=1):
        bigL = [v[0] for v in sorted(headerTable.items(), key=lambda p: p[1][0])]
        for basePat in bigL:
            newFreqSet = prefix.copy()
            newFreqSet.add(basePat)
            freqItemSet.append(newFreqSet)
            condPattBases = findPrefixPath(headerTable[basePat][1])
            (myCondTree, myHead) = createFPtree(condPattBases, headerTable[basePat][0])
            if myHead is not None:
                mineTree(myHead, newFreqSet, freqItemSet, minSup)
    if hasattr(transactions, 'tolist'):
        transactions = transactions.tolist()
    item_counts = Counter(itertools.chain.from_iterable(transactions))
    filtered_items = [[item for item in transaction if item_counts[item] >= min_sup] for transaction in transactions]
    sorted_transactions = [sorted(transaction, key=lambda x: (-item_counts[x], x)) for transaction in filtered_items]
    headerTable = createHeaderTable(sorted_transactions, min_sup)
    if headerTable is None:
        return []
    fpTree = createFPtree(sorted_transactions, headerTable)
    freqItemSet = []
    mineTree(headerTable, set([]), freqItemSet, min_sup)

    def format_items(items):
        return tuple(sorted((item for item in items)))
    result = [format_items(itemSet) for itemSet in freqItemSet]
    return sorted(result, key=lambda x: (len(x), x))

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def recursive_round(input_data):
        if isinstance(input_data, (float, np.float64)):
            return np.round(input_data * (1 - input_data), 4)
        elif isinstance(input_data, (list, np.ndarray)):
            return [recursive_round(x) for x in input_data]
    z_array = np.array(z) if not isinstance(z, np.ndarray) else z
    gradient = recursive_round(z_array)
    if isinstance(z, float):
        return gradient
    elif isinstance(z, list):
        return gradient
    elif isinstance(z, np.ndarray):
        return np.array(gradient).tolist() if z.ndim > 0 else gradient[0]

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

