import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    labels = np.unique(y_train)
    n_features = X_train.shape[1]
    feature_values = [np.unique(X_train[:, i]) for i in range(n_features)]
    empirical_dist = defaultdict(lambda : defaultdict(float))
    for (x, y) in zip(X_train, y_train):
        for (i, feature_value) in enumerate(x):
            empirical_dist[i, feature_value][y] += 1
    for i in range(n_features):
        for val in feature_values[i]:
            for label in labels:
                empirical_dist[i, val][label] /= len(y_train)
    weights = defaultdict(lambda : defaultdict(float))
    for i in range(n_features):
        for val in feature_values[i]:
            for label in labels:
                if empirical_dist[i, val][label] > 0:
                    weights[i, val][label] = math.log(empirical_dist[i, val][label])

    def calculate_z(x, weights, labels, feature_values, n_features):
        """Calculate the normalization factor Z(x) for the given x."""
        z_x = 0.0
        for y in labels:
            score = 0.0
            for i in range(n_features):
                val = x[i]
                score += weights[i, val][y]
            z_x += math.exp(score)
        return z_x

    def calculate_new_weights(weights, labels, empirical_dist, feature_values, n_features):
        """Iteratively update weights using Generalized Iterative Scaling algorithm."""
        new_weights = weights.copy()
        converged = False
        iters = 0
        while not converged and iters < n_iter:
            largest_update = 0
            for k in new_weights:
                total_diff = 0
                for label in labels:
                    total_diff += empirical_dist[k][label] * (math.exp(new_weights[k][label]) / calculate_z(k[1], new_weights, labels, feature_values, n_features) - 1)
                for label in labels:
                    current_weight = new_weights[k][label]
                    new_weights[k][label] = math.log(math.exp(current_weight) * (empirical_dist[k][label] / calculate_z(k[1], new_weights, labels, feature_values, n_features)))
                    update_size = abs(new_weights[k][label] - current_weight)
                    largest_update = max(largest_update, update_size)
            iters += 1
            converged = largest_update < epsilon
        return new_weights
    weights = calculate_new_weights(weights, labels, empirical_dist, feature_values, n_features)
    predictions = []
    for x in X_test:
        best_label = labels[0]
        best_score = float('-inf')
        for y in labels:
            score = 0.0
            for i in range(n_features):
                score += weights[i, x[i]][y]
            if score > best_score:
                best_label = y
                best_score = score
        predictions.append(best_label)
    return predictions

import numpy as np
def euclidean_distance(x, y):
    """Compute the Euclidean distance between two points."""
    return np.linalg.norm(x - y)
def pairwise_euclidean_distances(data):
    """Compute the full pairwise Euclidean distance matrix."""
    n_samples = data.shape[0]
    distances = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            distances[i, j] = euclidean_distance(data[i], data[j])
    return distances
def k_distance(distances, k):
    """Compute the k-distance and k-nearest neighbors for each sample."""
    sorted_distances = np.sort(distances, axis=1)
    k_distances = sorted_distances[:, k]
    knn_indices = np.argsort(distances, axis=1)[:, :k + 1]
    return (k_distances, knn_indices)
def reachability_distance(distances, k_distances, k, idx_p, idx_q):
    """Compute reachability distance between points p and q."""
    return max(k_distances[idx_q], distances[idx_p, idx_q])
def local_reachability_density(distances, k_distances, k, knn_indices, idx_p):
    """Compute the local reachability density of point p."""
    reach_dists = [reachability_distance(distances, k_distances, k, idx_p, idx_q) for idx_q in knn_indices[idx_p]]
    lrd = k / sum(reach_dists)
    return lrd
def local_outlier_factor(data, k, epsilon=1.5):
    """Calculate the LOF for given data."""
    distances = pairwise_euclidean_distances(data)
    (k_distances, knn_indices) = k_distance(distances, k)
    lrds = np.array([local_reachability_density(distances, k_distances, k, knn_indices, i) for i in range(len(data))])
    lofs = [np.mean([lrds[j] for j in knn_indices[i]]) / lrds[i] for i in range(len(data))]
    outliers = [i for (i, lof) in enumerate(lofs) if lof > epsilon]
    return (np.round(lofs, 4).tolist(), outliers)
