def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):

    def kernel_function(x1, x2):
        if kernel == 'linear':
            return np.dot(x1, x2)
        elif kernel == 'rbf':
            return np.exp(-np.linalg.norm(x1 - x2) ** 2 / (2 * sigma ** 2))
        else:
            raise ValueError("Unsupported kernel type. Choose 'linear' or 'rbf'.")
    (n_samples, n_features) = data.shape
    alphas = np.zeros(n_samples)
    bias = 0.0
    for t in range(1, iterations + 1):
        learning_rate = 1 / (lambda_val * t)
        grad_alpha = np.zeros(n_samples)
        for i in range(n_samples):
            sum_kernel = 0.0
            for j in range(n_samples):
                sum_kernel += alphas[j] * labels[j] * kernel_function(data[i], data[j])
            grad_alpha[i] = 1 - labels[i] * (sum_kernel + bias)
        alphas = alphas * (1 - learning_rate * lambda_val)
        for i in range(n_samples):
            if grad_alpha[i] > 0:
                alphas[i] += learning_rate * labels[i]
        sum_kernel_bias = 0.0
        for i in range(n_samples):
            sum_kernel_bias += alphas[i] * labels[i] * kernel_function(np.zeros(n_features), data[i])
        bias = bias + learning_rate - learning_rate * lambda_val * bias - learning_rate * sum_kernel_bias
    alphas = np.round(alphas, 4)
    bias = np.round(bias, 4)
    return (alphas.tolist(), bias.tolist())

